style.css:
@-moz-document domain(tinypic.com)
{
/*Main*/
div[class="member-container"], div[class="header-container"], div[class="browse columns"], div[class="about"], div[id="site-info"] {display:none!important;}
/*Uploaded*/
div[class="alert"], div[class="info"], li[id="shareMS"], li[id="addFavs"], li[id="edit"], li[id="twitter"], a[title="Share"], img[alt="Share"] {display:none!important;}
}
