style.css:
@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
body.v6 .page_content {
    width: 80%;
    margin: 0 auto
}
/*nav*/
div#global_header,
body.v6 .supernav_content,
body.v6 #global_header,
body.v6 #global_header .content {
    background: #111721;
}
div#global_header .content {
    width: 90%;
    margin: 0 auto;
    display: flex;
    justify-content: center;
    background: #111721;
}
#global_header .supernav_container {
    display: flex;
    left: 0;
    position: relative;
}
div#global_header .menuitem {
    padding-right: 4em;
}
#global_header > div > div.supernav_container > div {
    top: 70px!important;
}
.supernav_content {
    background: #171a21;
    box-shadow: 0 1px 5px #000;
    border-radius: 4px;
    max-width: 145px;
    width: 100%;
}
.supernav_content a.submenuitem {
    line-height: 2em;
}
/*thread header*/
.apphub_HomeHeader {
    margin: 0 auto 8px auto;
    max-width: 100%;
}
.apphub_HomeHeaderContent {
    padding-top: 10px;
    max-width: 100%;
}
body > div.responsive_page_frame.with_header > div.responsive_page_content > div.responsive_page_template_content > div.pagecontent > div > div.apphub_HomeHeader > div.apphub_HomeHeaderContent,
#ModalContentContainer > div.apphub_background > div.apphub_HomeHeader > div.apphub_HomeHeaderContent {
