style.css:
@-moz-document url(https://sgo.volganet.ru/about.html) {
  /*Загрузка*/
  div.page-preloader {
  background: linear-gradient(black, #1f3b00);
  }
  div.page-preloader .spinner:before {
    color :#cef300;
  }
  div.page-preloader .spinner>.text {
    color: #cef300;
  }
 /*Страница регистрации*/
  html body {
background: linear-gradient(black, #1f3b00);
}
html body .centered .box-form {
   border: 1px solid #919800;
}
.entry-form .img-logo { /*логотип*/
  background: none;
}
html body .message-form {
background: #006600;
}
.form-horizontal {
background: #006600;
}
 html body .centered .entry-form .sectiontable .form-horizontal {
 background: #060;
 }
html body .message-form .form-horizontal .select-main span {
    background: #003300;
}
html body .message-form .form-horizontal .select-main select {
  background: #003300;
}
html body .message-form .form-horizontal .select-main select:focus {
background: #003300;
}
html body .centered .entry-form .tabs-form li.current {
    background: #006600;
  border: 1px solid #919800;
}
html body .centered .entry-form .tabs-form li {
  background: #091000;
  border-bottom: 1px solid #6e7400;
}
html body .message-form input.control-input.col-md-6 {
  background: #003300;
}
html body .message-form .form-horizontal .two-lines-decoration {
border-bottom: 1px solid #003300;
border-top: 1px solid #003300;
}
  html body .message-form input.control-input.col-md-6:focus{
  background-color:#025502;
  }
html body .row-center .button-login>.button-login-title { /*кнопка*/
  background: rgb(255 148 0);
  color: black;
  background-image: linear-gradient(0);
  border: 0;
  -webkit-box-shadow: none;
  box-shadow: none;
  transition: 1s;
}
  html body .row-center .button-login-title:hover{
  background-color: #c47100;
  color: black;
  }
  /*Конец страницы регистрации*/}

@-moz-document domain(volganet.ru) {
  /*Загрузка*/
  div.apploader{
  background: black;
  }
  .school div.apploader span.arrows:before, .school div.content-preloader span.arrows:before, .school span.content-page-preloader:before {
    color: #dae804;
}
  div.apploader span.title{
   color: #dae804;
  }
  /*Логотип(удален)*/
  .header .logo {
   box-shadow: none;
  }
.header .logo-school {
  background: none;
  width: 0;
}
  /*Основной фон*/
  .bg-lightgray {
    background:  linear-gradient(#1f3b00 -20%, black, #1f3b00 120%); 
}
  body {
  background: linear-gradient(#1f3b00 -20%, black, #1f3b00 120%);
  }
 
 /*Подвал*/
  .footer {
  background-color: rgba(0,0,0,0);
  color: black;
  }
  .footer .logo-footer {
  width: 0px;
  background: none;
  }
 /*конец подвала*/
  
 /*Доска объявлений*/
  .block-content { /*фон*/
  min-height: 540px;
  }
  /*сообщений нет*/
  .school .alert-info {
    border-color: #006600;
    color: #b8c11b;
  }
  .school .alert-info, .school .bg-info {/*фон сообщений*/
    background-color: #00000000;
}  /*Конец сообщений нет*/
 /*Конец доски*/
  /*Шапка, название корпуса, пользователь*/
  .school .header{
    background: none;
  }
  .header .block-logo {
  background: none;
  }
  .school .block-personal-cabinet .number-employees-in-system p a span+span {
    color: #a9c315;
}
  /*Сокращение списка навигации*/
  .school .btn-menu {
    background-color: #006600;
    border: 1px solid #003300;
}
  .btn-menu .btn-menu-body {
  background: #003300;
  }
  
   /*Название активного раздела*/
  .school .block-content h1 {
    color: #dae804;
    background: #006600;
}
 /*Кнопки "почта" "форум"*/
  .school .block-content .top-right-menu a {
    color: #000000;
}
  .school .block-content .top-right-menu a:hover {
    color: #004701;
}
  .numberMail {/*Кол-во писем*/
  background: #343434;
  }
  /*кнопка Назад*/
  .school .block-content a.active, .school .block-personal-cabinet ul li.separator button>div#flip_container {
    color: #18b71a;
}
 
  /*Навигация*/
  .school .header .navbar {
    border: 2px #002200 solid;
    border-radius: 3px;
    background: #003300;
}
  /*текст в навигации и кнопки*/
  .school .header .navbar .navbar-nav .dropdown a, .school .header .navbar .navbar-nav li a {
    color: #a1a1a1;
     transition: .5s;
}
  /*Активная кнопка*/
  .school .header .navbar .navbar-nav .active a {
    background: #006600;
    color: white;
}
  /*Кнопки без вариантов(:active)*/
.school .header .navbar .navbar-nav li>a:active{
  background: #006600;
  /* border: 1px solid #efff00; 
    border-radius: 5px;*/
}
  /*(:focus, :hover)*/
  .school .header .navbar .navbar-nav li>a:focus, .school .header .navbar .navbar-nav li>a:hover, .navbar-nav > li:hover:hover {
  background: #022200;
    color: #fff;
}
   /*Кнопки c вариантaми*/
.school .header .navbar .navbar-nav .dropdown:active>a, .school .header .navbar .navbar-nav .dropdown:focus>a, .school .header .navbar .navbar-nav .dropdown:hover>a, .school .header .navbar .navbar-nav .dropdown:visited>a, .school .header .navbar .navbar-nav li:active>a, .school .header .navbar .navbar-nav li:focus>a, .school .header .navbar .navbar-nav li:hover>a, .school .header .navbar .navbar-nav li:visited>a, .school .header .navbar .navbar-nav>.open>a {
  background: #022200;
    color: #fff;
  }
 .school .header .navbar .navbar-nav .dropdown-menu li a {
    background: #006600;
    color: #a1a1a1;
   transition: .5s;
}
  .school .header .navbar .navbar-nav .dropdown a:active, .school .header .navbar .navbar-nav .dropdown a:focus, .school .header .navbar .navbar-nav .dropdown a:hover, .school .header .navbar .navbar-nav .dropdown a:visited, .school .header .navbar .navbar-nav li a:active, .school .header .navbar .navbar-nav li a:focus, .school .header .navbar .navbar-nav li a:hover, .school .header .navbar .navbar-nav li a:visited {
    color: white;
    background: #022200;
  }
  .dropdown-menu {
  background-color: #fff0;
  }
  .navbar-nav>li>.dropdown-menu {
    border-radius: 0px 10px 10px 10px;
  }
   /*Активная страница в списке навигации*/
  .school .header .navbar .navbar-nav .bg-primary, .school .header .navbar .navbar-nav .dropdown-menu>.active>a, .school .header .navbar .navbar-nav .dropdown-menu>.active>a:focus, .school .header .navbar .navbar-nav .dropdown-menu>.active>a:hover {
    background-color: #007e07;
    background:  #003300;
    color:white;
}
}
