style.css:
@-moz-document domain("mail.yahoo.co.jp") {
#root > div:first-child > div:first-child {
    display: none;
  }
  #TEMPLA_WRAPPER {
    position: absolute;
    right: 10em;
  }
  #TEMPLA_MH_VDOM {
    min-height: fit-content !important;
  }
  #mhHeadLine {
    display: none;
  }
  #msthd #mhMain {
    margin-top: 0 !important;
  }
  #tagYadsListTop,
  #tagYadsSideColumn,
  #tagYadsDetail {
    display: none;
  }
  .mainColumn > div:first-child > div:first-child,
  .mainColumn > div:last-child {
    display: none;
  }
  .mainColumn > div:first-child > div:last-child,
  .mainColumn > div:first-child {
    display: flex;
  }
}
