  color: red !important;
  text-decoration: inherit !important;
}

.card-item-link
{
  text-decoration: inherit !important;
}

.rbx-nav-collapse
{
  visibility: hidden !important;
  width: 0px !important;
}

.rbx-navbar:hover
{
  background-color: transparent !important;
}

a:hover
{
  background-color: transparent !important;
  text-decoration: underline !important;
}

body
{
  background-size: 100% auto;
  background-position: center top;
  background-attachment: scroll;
  background-repeat: no-repeat;
  background-color: #ffffff;
}

#navbar-universal-search
{
  visibility: visible !important;
  width: 920px !important;
  margin: 10px !important;
  z-index: 1 !important;
  position: fixed !important;
  top: -160px !important;
  opacity: 1;
  padding-bottom: 20px;
  transition: all 0.5s;
}

#navbar-universal-search:hover
{
  top: -130px !important;
  padding-bottom: initial;
}

.navbar-brand,
.rbx-nav-notification,
.rbx-icon-nav-menu,
.rbx-nav-collapse,
.rbx-navbar-header
{
  visibility: hidden;
  width: 0px !important;
  margin: 0px !important;
  padding: 0px !important;
}

#header
{
  left: calc(50% - 495px);
  height: 75px !important;
  width: 970px !important;
  background-image: url("http://i.imgur.com/KNIT7m9.jpg");
  background-size: 100% auto;
  background-repeat: no-repeat;
  position: absolute !important;
  z-index: 1000 !important;
  top: 120px !important;
  border-color: black;
  border-style: solid;
  border-width: 1px;
  border-bottom-style: none;
}

li[ng-show*="profileHeaderLayout.mayUpdateStatus"]
{
  background-image: url("http://bit.ly/roblox-2009-users");
  background-size: 0px 0px;
}

#GamesListsContainer,
.games-list-container
{
  background-color: transparent !important;
}

.content
{
  margin-top: calc(120px + 75px);
  margin-left: calc(50% - 495px) !important;
  margin-right: calc(50% - 495px) !important;
  width: 970px !important;
  max-width: none !important;
  float: left !important;
  background-color: transparent !important;
}

#SearchControls .content,
#newmessage .content
{
  margin: initial !important;
  width: initial !important;
  float: initial !important;
}

#BodyWrapper
{
  width: 970px !important;
  margin-top: 200px !important;
}

#RepositionBody #Body
{
  width: 970px !important;
  margin-left: -20px;
}

.container-footer
{
  position: absolute;
  top: initial !important;
  left: 100px !important;
  right: 100px !important;
  width: auto !important;
}

#navigation
{
  position: absolute !important;
  font-family: "Comic Sans MS", "Arial";
  background-color: #6F99CB;
  color: white !important;
  width: 970px !important;
  height: 40px;
  left: calc(50% - 495px);
  top: calc(75px + 120px);
  overflow: hidden !important;
  box-shadow: none !important;
  border-color: black;
  border-style: solid;
  border-width: 1px;
}

#navigation #mCSB_1_scrollbar_vertical
{
  display: none !important;
  opacity: 0 !important;
}

#navigation > ul
{
  visibility: hidden !important;
  position: fixed !important;
}

#navigation div
{
  visibility: visible !important;
}

#navigation .rbx-scrollbar
{
  overflow: visible !important;
}

.nav
{
  font-family: "Comic Sans MS", "Arial" !important;
  z-index: 2 !important;
  top: 77px;
  margin: 0px !important;
  left: calc((50% - 430px) + 120px) !important;
  line-height: initial !important;
  width: 280px;
}

.rbx-navbar
{
  position: absolute;
}

.nav li a
{
  margin-left: 8px !important;
  margin-right: 8px !important;
  display: inline-block !important;
}

.nav li
{
  width: auto !important;
  margin-top: 2px !important;
}

.buy-robux
{
  position: fixed !important;
  visibility: hidden !important;
  z-index: -1 !important;
}

#nav-home,
#nav-profile,
#nav-friends,
#nav-character,
#nav-inventory,
#nav-trade,
#nav-group,
#nav-forum,
#nav-blog,
#nav-shop
{
  color: white;
  float: left;
  padding-left: 10px;
  padding-right: 10px;
}

#nav-profile
{
  font-size: 0px !important;
  min-width: 94px !important;
  margin-right: 160px;
}

#nav-profile span::after
{
  content: "My ROBLOX" !important;
  font-size: 16px !important;
}

#nav-home:hover,
#nav-profile:hover,
#nav-character:hover,
#nav-inventory:hover,
#nav-trade:hover,
#nav-group:hover,
#nav-forum:hover,
#nav-blog:hover,
#nav-shop:hover
{
  color: white;
  text-decoration: underline !important;
}

#mCSB_1_container ul li a:hover
{
  color: white !important;
  text-decoration: underline !important;
}

#nav-message,
#nav-friends
{
  transition: background-color 0.5s, left 0.5s;
  opacity: 1;
  z-index: 2000 !important;
  position: fixed !important;
  left: -80px;
  color: black;
  top: 0px !important;
  width: 130px;
  text-align: left !important;
  padding: 0px !important;
}

#nav-friends
{
  top: 40px !important;
}

#nav-message span,
#nav-friends span
{
  color: black !important;
}

#nav-message .rbx-highlight,
#nav-friends .rbx-highlight
{
  color: white !important;
}

#nav-message:hover,
#nav-friends:hover
{
  left: 0px;
  color: black !important;
}

#nav-character
{
  margin-left: 100px;
}

.icon-nav-home,
.icon-nav-profile,
.icon-nav-message,
.icon-nav-friends,
.icon-nav-charactercustomizer,
.icon-nav-inventory,
.icon-nav-trade,
.icon-nav-group,
.icon-nav-forum,
.icon-nav-blog,
.icon-nav-shop,
#nav-shop
{
  width: 0px !important;
  display: none !important;
}

.game-item .rbx-votes
{
  visibility: hidden !important;
  position: fixed !important;
  left: -100% !important;
}

.rbx-left-col,
.rbx-scrollbar,
#mCSB_1_container
{
  width: auto !important;
}

#mCSB_1_container
{
  width: 900px !important;
  height: 30px !important;
  margin-left: calc(50% - 425px);
  top: 0px !important;
  overflow: visible !important;
}

#mCSB_1_container ul
{
  overflow: visible !important;
}

#mCSB_1_container ul li
{
  overflow: visible !important;
}

#mCSB_1_container ul li span
{
  text-decoration: inherit !important;
}

.rbx-upgrade-now,
.rbx-nav-sponsor,
#nav-home,
.rbx-text-notes
{
  position: fixed !important;
  left: -100% !important;
  z-index: -1 !important;
  visibility: hidden !important;
  margin: 0px !important;
}

#nav-blog
{
  font-size: 0px !important;
  margin-top: 6px;
}

#nav-blog::after
{
  content: "News";
  font-size: 16px;
  margin-top: 15px !important;
}

#navigation ul .rbx-lead
{
  position: absolute !important;
  z-index: 100 !important;
  top: 0px !important;
  overflow: visible !important;
}

li
{
  margin-top: 0px !important;
  margin-bottom: 0px !important;
}

ul
{
  padding: 0px !important;
}
