style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {

ytd-watch-flexy #info.ytd-video-primary-info-renderer {
    flex-wrap: wrap !important;
}

ytd-watch-flexy #info.ytd-video-primary-info-renderer #menu-container.ytd-video-primary-info-renderer {
    margin-left: auto !important;
}
/*[[bug]]*/
/*[[word]]*/
}

options.value:
ytd-watch-flexy #info.ytd-video-primary-info-renderer ytd-menu-renderer.ytd-video-primary-info-renderer ytd-toggle-button-renderer:nth-child(2) yt-formatted-string{
        display:none !important;
}/* dislike */

ytd-watch-flexy #info.ytd-video-primary-info-renderer ytd-menu-renderer.ytd-video-primary-info-renderer ytd-button-renderer yt-formatted-string{
        display:none !important;
}/* share, clip, save */

ytd-watch-flexy #info.ytd-video-primary-info-renderer ytd-menu-renderer.ytd-video-primary-info-renderer ytd-download-button-renderer yt-formatted-string{
        display:none !important;
}/* download */
