style.css:
@-moz-document domain("email.hs-coburg.de") {
/*
#########################################
#	Author: HiloadProgrammer            #
#	Version: 1.51.3                     #
#########################################
*/
/*
  Color palette reference
  https://uxdesign.cc/dark-mode-ui-design-the-definitive-guide-part-1-color-53dcfaea5129
  https://miro.medium.com/max/1080/1*ex4Z8MlCOzpHup5AEMU-2Q.png
*/
/* ######## Sign in ######## */
#mainLogonDiv {
  background-color: #222222;
}

#userNameLabel {
  color: white;
}

#passwordLabel {
  color: white;
}

/*######## General ##########*/
body {
  background-color: #222222;
}

span {
  color: white;
}

/*---General classes */
/* hovered owa svgs */
.wf-family-owa.ms-font-color-neutralDark {
  color: #627D98;
}

.ms-bg-color-themeSecondary {
  background-color: #627D98;
}

/*######## Sidebar ##########*/
[role="heading"] {
  color: white;
}

[role="treeitem"] {
  color: white;
}

[role="treeitem"]:hover {
  background-color: #9AA5B1;
}

/* if selected do not change background on hover */
[role="treeitem"].ms-bg-color-themeLight:hover {
  background-color: #334E68;
}

div._lv_B span {
  color: white;
  font-weight: bold;
}

.ms-bg-color-themeLight {
  background-color: #334E68;
}

/* styling for unred mails number */
[role="treeitem"] span._n_17, [role="treeitem"] span._n_d6 {
  color: red;
  font-weight: bold;
}

/*######## Email list ##########*/
/*---mail list navigation*/
._n_v2[role="menu"][aria-label="E-Mail-Filter"] {
  background-color: #222222;
}

._n_v2[role="menu"][aria-label="E-Mail-Filter"] [role="menuitemradio"]:hover {
  text-decoration: underline;
}

/*---list */
._lv_q:hover {
  background-color: #616E7C;
}

/* this is set to avaoid to override the background if a mail is selected */
[aria-selected="true"] ._lv_q:hover {
  background-color: rgb(51, 78, 104);
}

/*######## Email History Dropdown ##########*/
/* sender receiver (email history) drop down*/
.ms-font-s {
  color: white;
}

.ms-font-xs {
  color: white;
}

.ms-bg-color-neutralLighter {
  background-color: #486581;
}

/*unread mail banner*/
.scrollContainer .ms-bg-color-themeSecondary {
  background-color: red;
}

/*######## User Account Dorpdown ##########*/
/*user account drop down*/
.ms-bg-color-white {
  /*background-color: #829AB1;/**/
  background-color: #243B53;
  /**/
}

/*text color of non selceted box*/
.ms-font-color-neutralSecondary {
  color: #F0F4F8;
}

/*Settings button header*/
div[role="banner"] {
  background-color: #243B53;
}

/*font color user account settings dropdown: user full name*/
.ms-font-color-neutralPrimary {
  color: white;
}

/*font color user account settings dropdown: user mail address*/
.headerMenuDropShadow ._pe_I._pe_h.bidi {
  color: white;
}

/*######## Sidebar Popup right ##########*/
/*settings popup right sidebar*/
div.popupPanel {
  background-color: #243B53;
}

/*Input boxes (example: "weiteres postfach hinzufuegen")*/
input.textbox {
  color: black;
}

.ms-font-weight-regular {
  color: white;
}

.ms-font-weight-semilight {
  color: white;
}

/*######## Input Boxes ##########*/
/*Input boxes (example: "weiteres postfach hinzufuegen") header*/
.ms-font-color-themePrimary {
  color: white;
  font-weight: bold;
}

/*######## Email Viewer ##########*/
._rp_e [role="list"] ._rp_w3 {
  padding: 10px;
}

._rp_e [role="list"] ._fc_3 {
  background-color: inherit;
}

/*Email viewer*/
.ms-font-color-neutralDark {
  color: black;
}

.conductorContent .ms-font-color-black {
  color: white;
}

[id='ItemHeader.ToContainer'] .ms-font-color-black {
  color: white;
}

[id='ItemHeader.ToContainer']:first-child {
  font-weight: bold;
}

/*search user dropdown*/
.ms-bg-color-themeLighter {
  background-color: #486581;
}

.ms-border-color-neutralLight {
  background-color: #243B53;
}

form .ms-border-color-neutralTertiary {
  background-color: #829AB1;
  /**/
  /*background-color: #243B53;/**/
}

/*This fixes the bug, that if the mail is to long, the text will be written
 on the dark theme background */
.conductorContent iframe {
  background-color: white;
}

/* Mail content text color fix */
[id="Item.MessageUniqueBody"] {
  background-color: white;
  padding: 10px;
}

[id="Item.MessagePartBody"] {
  color: black;
}

[id="Item.MessagePartBody"] span {
  color: black;
}

[id="Item.MessagePartBody"] div {
  color: black;
}

[id="Item.MessagePartBody"] p {
  color: black;
}

/*######## Mail reply ########*/
.conductorContent .ms-font-color-black[aria-label="Nachrichtentext"] {
  color: black;
}

/* reply viewer of seperat window */
div[class*="_rp_Q"][role="region"] {
  background-color: white;
  color: black;
  padding: 10px;
}

div[class*="_rp_Q"][role="region"] * {
  color: black;
}

/*document.querySelector('[tempid="id=readingpaneView;path=Applications/Views/Mail/MailModuleView.htm"] .conductorContent').innerHTML*/
/*######## Calendar ##########*/
/*calendar header fix*/
.popupPanel.panelPopupShadow ._cx_b1 {
  background-color: #102A43;
}

/*--- sidbare left */
/* selected kalender tree item*/
[tempid*="id=calendarpicker"] [style="background-color: rgb(192, 233, 255);"] .ms-font-color-neutralSecondary {
  color: black;
}

/* kalendar additional kalendars */
/*
	If a calendar is selected, the background style of the kalendar element will be changed via the style attribute
*/
[style*="background-color:"] ._cp_j, [style*="background-color:"] ._fc_3 {
  color: black;
}

/* month selection (nav)*/
._ce_Q1.ms-font-color-neutralPrimary:hover {
  background-color: #616E7C;
}

/* current month */
[aria-checked="true"] ._ce_Q1 {
  background-color: #334E68;
}

/* mini calendar day*/
._cd_o:hover {
  background-color: #616E7C;
}

/*--- Calendar days */
._ce_A1 span {
  color: black;
}

/* days */
/** needs more debugging for this feature
._ce_22:hover{
	background-color: #616E7C;
}

._ce_22 span:hover{
	background-color: #616E7C;
}
**/
/* selected day day-text color */
._ce_A1 .ms-bg-color-themeLight ._cb_O1 {
  color: white;
}

/* current day border color */
._ce_A1 .ms-border-color-themePrimary {
  border-top-color: red;
}

/* day appointments */
._ce_A1 ._ce_P1 {
  background-color: #334E68;
  color: white;
}
._ce_P1{
  border-color: white;
  border-style: solid;
  border-width: 1px;
}

/* calendar view appointment text */
._cb_o1._cb_p1 {
  color: white;
}

/* calendar view appointment date */
._ce_P1 ._cb_n1 {
  color: white;
}

/* calendar filter */
._cb_R ._cb_K ._cb_J {
  color: black;
}

/*---sidebar right */
/* appointments */
._cb_j, ._cb_j ._cb_p {
  color: black;
}

._cb_r {
  background-color: #334E68;
  border-color: #334E68;
}

/* appointment container */
._cb_D {
  background-color: #627D98;
}

/*---appointment reminder*/
._f_w1, ._f_w1 ._f_v1 {
  background-color: #334E68;
}

._f_a1 * {
  color: black;
}

/* ########  Person View ######## */
/*--- sidebar left */
._ph_9[role="treeitem"] ._ph_8:hover {
  /*#616E7C*/
  background-color: #616E7C;
}

/*current selected*/
._ph_9[role="treeitem"] ._ph_8.ms-bg-color-themeLight:hover {
  /*#616E7C*/
  background-color: #334E68;
}

/*--- nav bar filter */
._ph_d1[role="menu"] {
  background-color: inherit;
}

/* ######## TODOs View ######## */
._s_n ._fce_W {
  background-color: #222222;
}

/*########  Settings View ########*/
#priNav ul li * {
  color: white;
}

#priNav .priSelected a {
  /*background-color: #616E7C;*/
  color: black;
}

#wrpdv * {
  background-color: #222222;
  color: white;
}

#wrpdv .usagePart {
  background-color: rgb(48, 206, 17);
}

#dlgModalError {
  background-color: #323F4B;
}

[role="menu"] {
  background-color: #323F4B;
}

[role="menu"] .FocusMenuItem span {
  color: black;
}

.searchPersonasHitHighlight .scrollContainer[role="button"] span[role="heading"] span .psearchHighlightPeopleHub {
  background-color: #9AA5B1;
  color: black;
}

.searchPersonasHitHighlight .scrollContainer[role="button"] span[role="heading"] span {
  color: white;
}

._rpc_2 {
  background-color: #616E7C;
}

._pe_7:hover {
  background-color: #616E7C;
  color: white;
}

._pe_I {
  color: white;
}

._pe_I .psearchHighlight {
  background-color: #9AA5B1;
}

/*rgb(48, 206, 17)*/

}
