style.css:
@-moz-document url-prefix('https://codecanyon.net/author_dashboard') {
    .page {
        font-family: system-ui;
        font-size: 15px;
    }

    .fixed-layout .content-s {
        float: left;
        width: 100%;
    }

    .box__heading {
        color: #DFDFDF;
    }
    .box--highlight-green {
        background-color: #3C3C3C;
        color: #DFDFDF;
        opacity: 0.9;
        border: none;
    }
    .hr-light {
        border-bottom: 1px solid #484746;
    }

    .cross-sell,
    .global-footer,
    .global-footer-quick-links, 
    .global-footer-quick-links__collapsed,
    .fixed-layout .sidebar-right, 
    .headerstrip-wrapper, 
    .author-comments-search-controls select,
    .page-tabs {
        display: none;
    }
    .page-section.-border-bottom {
        border: none;
    }

    .comments-search-controls__results-count {
        font-size: 15px;
    }

    .comment__container {
        width: 650px;
        border: 1px solid #484746;
        border-radius: 12px;
        box-shadow: 0 0 2px #1b1b1a;
    }
    .comment__header .e-text-label {
        background-color: #31302F;
        color: #E0DFDF;
    }
    .comment {
        padding-left: 40px;
    }
    .comment__username {
        font-weight: 400;
        color: #E99264;
        font-size: 13px;
    }

    .comment__user {
        margin-right: 0px;
    }

    .e-text-label.-color-grey-dark, .-color-grey-dark.e-text-label--reversed {
        color: #FFE800;
        letter-spacing: 1px;
        background: #31302F;
        margin-left: 4px;
        font-weight: 500;
    }

    .comment__header small {
        font-size: 0px;
        display: block;
        margin-top: 5px;
    }

    .comment__header small a {
        color: #DFDFDF;
        font-weight: 700;
        font-family: system-ui;
        font-size: 20px;
        line-height: 22px;
        max-width: 70%;
        display: block;
        overflow: hidden;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }

    .-theme-elite .page-section__background, .e-box {
        display: none;
    }
    .t-heading.-color-dark, .t-heading {
        color: #31302F;
    }
    .t-body.-color-dark, .t-body, .page-tabs a {
        color: #b1b1b1;
    }
    section.page-section.h-pt3.h-mb3.h-pb0.-border-bottom.-theme-elite {
        margin-bottom: 0 !important;
        border-bottom: none;
    }
    section.page-section.h-pt3.h-mb3.h-pb0.-border-bottom.-theme-elite {
        background-image: url(https://public-assets.envato-static.com/assets/common/bg-page-section-4b4d6232f7d65b1eda49f92911855825b81774b95534ab3b8eb62e1decb37d71.png);
        background-size: 400px;
    }
    .content-main--basic {
        background: #31302F;
    }
    .grid-container.h-mb3 {
        margin-top: 24px;
    }
    .header-categories {
        background-color: #363636;
        border-bottom: 1px solid #292929;
    }
    .header-categories__main-link, .header-categories__main-link--empty {
        color: #b1b1b1;
    }
    .header-site-floating-logo__codecanyon {
        background-color: #1a1a1a;
    }
    .header-categories__search--experiment {
        border-left: 1px solid #666666;
    }
    input#term, .-border-light button.search-field__button {
        background: #484848;
        border-color: #484848;
        color: #c7c7c7;
    }
    .-border-light button.search-field__button {
        font-size: 15px;
    }
    .e-box {
        background-color: #323233;
        border: 1px solid #363636;
    }
    .e-box.-stacked {
        background: #454545;
    }
    .box--hard-top {
        background-color: #323233;
        color: #bababa;
        border: none;
    }

    .t-icon-list a, .t-icon-list a:hover, .user-html a, .user-html a:hover {
        color: #008DFF;
    }
    .comment__initial {
        background-color: #31302F;
        color: #E0DFDF;
        border-radius: 12px 12px 0 0;
        padding: 10px 20px 0px;
    }
    .comment-reply {
        background-color: #31302F;
        color: #E0DFDF;
        border-bottom: 1px solid #464544;
        padding: 15px 20px 10px;
    }
    .comment__new-reply img{
        border-radius: 40px;
    }
    .comment-reply:last-child {
        border-radius: 0 0 12px 12px;
        border-bottom: none;
    }
    .comment-reply:last-child .comment-reply__avatar { 
        display: none;
    }
    .comment-reply:last-child .comment__new-reply { 
        margin-left: 0px;
    }

    .comment .badges, .badge-exposer {
        display: none;
    }
    .comment .badges, .badge-exposer {
        font-size: 15px;
    }
    .avatar img {
        border-radius: 90px;
        width: 60px;
        height: 60px;
    }
    .form-submit button:last-child, button.e-btn.-color-muted {
        background: #6F66D3;
        font-weight: 600;
        font-size: 16px;
        padding: 6px 40px;
    }


    .comment__header {
        opacity: 1;
        margin-bottom: 15px;
    }
    .comment__header:hover {
        opacity: 1;
    }

    .comment-reply .comment__header {
        opacity: 0.3;
        padding-bottom: 0.2em;
    }
    .comment-reply .comment__header:hover {
        opacity: 1;
    }
    .comment-inline-form textarea {
        margin-top: 10px;
        background: #454545;
        border: none;
    }

    button.comment__action {
        margin: 0 10px;
        font-size: 1.4em;
    }
    .btn-icon.post-reply:after{
        display: none;
    }

    button.comment__action .js-post-mark {
        color: #00FA0D;
        opacity: 0.8;
    }
    .js-post-flag i{
        color: #FFE800;
        font-size: 1.3em;
        margin-left: 5px;
    }

    textarea.js-comment-new-reply-field.flexible-textarea {
        color: #DFDFDF;
        background: #3c3c3c;
        border: 1px solid #484746;
        font-size: 15px;
        height: 100px;
        padding: 15px;
    }
    .author-comments-search-controls input[type='search'] {
        border-radius: 5px;
        padding: 4px 8px 4px;
        vertical-align: 1.5px;
        color: #c7c7c7 !important;
        border: none;
        font-size: 15px;
        width: 405px;
    }
    .page-tabs .selected a {
        color: #ffffff;
        background: #666666;
        border: none
    }
    .page-tabs a:hover, .page-tabs a:focus {
        border: none;
    }
    select.slt {
        background: #484848;
        color: white;
        border: none;
        vertical-align: 2px;
        font-size: 15px;
    }
    .upload-category-selector__select, .upload-category-selector__btn {
        vertical-align: bottom;
    }
    .upload-category-selector__select {
        margin-right: 5px;
    }
    .e-box.-stacked.-radius-top.h-p2, .e-box.-radius-bottom.h-mb2.h-p2.h-pb3, .box--topbar:nth-of-type(3), .box--hard-top.new-typography:nth-of-type(4) {
        display: none;
    }
    hr {
        background-color: #454545;
    }
    .avatar img[src="https://public-assets.envato-static.com/assets/common/icons-buttons/default-user-b8d0c2225ee9d9e68b5d80cd0ed6b7800c0135da303b735b777ac5a39f778a69.jpg"], .comment-reply__avatar img[src="https://public-assets.envato-static.com/assets/common/icons-buttons/default-user-b8d0c2225ee9d9e68b5d80cd0ed6b7800c0135da303b735b777ac5a39f778a69.jpg"] {
        filter: invert(100%) brightness(1.2);
    }
    .cross-sell {
        margin-top: 0;
    }
    .comments-search-controls__results-count {
        color: #ababab;
        font-size: 13px;
        margin-right: 2px;
    }
    .comment-reply__avatar img {
        border-radius: 40px;
    }
}
