style.css:
@-moz-document regexp("^https://(?:testnet\\.|devnet\\.)?maiar\\.exchange.*") {
    /*
     * Created by Andrei -> under development.
     */
    /*
     * Resources:
     - https://media.giphy.com/media/8L1Ln4gOOabZIz0Q0s/giphy.gif via EA SPORTS FIFA on GIPHY
     */
    :root {
        --light-theme-color: #080;
        --light-theme-2nd-color: #a00;
        --dark-theme-color: Darkgreen;
        /* background */
        --background-left1: #060;
        --background-left2: #080;
        --background-right1: #c00;
        --background-right2: #a00;
        --background-right3: #800;
        /* elements */
        --li-color: var(--dark-theme-color);
        --button-color: var(--dark-theme-color);
        --button-border-color: var(--dark-theme-color);
        --feature-icon-color: var(--dark-theme-color);
        --feature-icon-border-color: var(--dark-theme-color);
        --card-arrow-color: var(--dark-theme-color);
        /* elements:hover */
        --button-hover-color: #0a0;
        --button-hover-border-color: var(--light-theme-2nd-color);
        --anchor-active-color: var(--light-theme-color);
        --anchor-hover-color: var(--light-theme-color);
        --dropdown-item-color: var(--light-theme-color);
        --dropdown-item-selected-color: var(--light-theme-color);
        /* #f6f */
        /* spinner */
        --spinner-color: var(--light-theme-color);
        /* logo */
        --logo-color: var(--light-theme-color);
    }

    div.layout {
        background-image: linear-gradient(to right, var(--background-left1), var(--background-left2), var(--background-right1), var(--background-right2), var(--background-right3)) !important;
    }
    /* Nav menu - main part */
    li.nav-item {
        padding-left: 1rem;
        padding-right: 1rem;
    }

    li.nav-item .nav-link {
        font-size: 1rem !important;
    }

    li.nav-item .nav-link.active {
        font-size: 1rem !important;
        background-color: var(--li-color);
    }

    li.nav-item .nav-link:hover {
        background-color: var(--button-hover-color) !important;
        color: white !important;
    }
    /* Nav menu - right side */
    nav button div.text-left {
        font-size: 1rem !important;
        font-weight: bold;
    }
    /* light on/off & 3 dots */
    nav.navbar button.btn-light {
        border-width: 0px;
    }

    nav.navbar button.btn-light:hover {
        color: white;
        background-color: var(--button-hover-color) !important;
    }

    nav.navbar button.btn-light:active {
        color: white;
        background-color: var(--button-color) !important;
    }

    nav.navbar button.btn-light:focus {
        color: white;
        background-color: var(--button-color) !important;
    }

    nav.navbar a.btn-light {
        border-width: 0px;
    }

    nav.navbar a.btn-light:hover {
        color: white;
        background-color: var(--button-hover-color) !important;
    }

    nav.navbar a.btn-light:active {
        color: white;
        background-color: var(--button-color) !important;
    }

    nav.navbar a.btn-light:focus {
        color: white;
        background-color: var(--button-color) !important;
    }

    nav .unlock-btn {
        background-color: var(--button-color);
        font-size: 1rem !important;
    }

    nav .unlock-btn:hover {
        background-color: var(--button-hover-color);
    }

    nav .unlock-btn div {
        margin-top: -0.25rem !important;
        color: white;
    }

    div#links-popover .popover-body .card .card-body a {
        font-size: 1rem !important;
    }

    div#links-popover .popover-body .card .card-body a:hover {
        font-size: 1rem !important;
        background-color: var(--anchor-hover-color);
        color: white !important;
    }

    div#links-popover .popover-body .card .card-body a:active {
        font-size: 1rem !important;
        background-color: var(--anchor-active-color);
        color: white;
    }

    .basic-popover#links-popover .dropdown-item.active {
        color: var(--dropdown-item-color);
    }
    /* drop downs */
    .styled-select__option--is-selected {
        background-color: var(--dropdown-item-selected-color) !important;
    }

    .styled-select__control {
        border-color: #343439 !important;
        background-color: #18191a !important;
    }

    .styled-select__control--is-focused {
        border-color: var(--dropdown-item-selected-color) !important;
        box-shadow: none;
    }

    .styled-select__control:hover {
        border-color: #9ba5b4 !important;
    }

    .styled-select__control.styled-select__control--is-focused {
        border-color: var(--dropdown-item-selected-color) !important;
        box-shadow: none;
    }
    /* buttons */
    .main-content .btn:not(.farm-action-btn) {
        font-size: 1rem !important;
        border-width: 0px;
    }

    .main-content .btn:hover {
        background-color: var(--button-hover-color) !important;
    }

    .btn.btn-primary {
        background-color: var(--button-color) !important;
    }

    .btn-primary:hover {
        background-color: var(--button-hover-color) !important;
    }

    .main-content .btn.btn-outline-primary-glow {
        border-color: var(--button-border-color) !important;
        background-color: rgba(0, 0, 0, 0);
        box-shadow: #faf 0px 0px 5px 0px inset, #faf 0px 0px 5px 0px !important;
    }
    /* Info cards */
    div.info-card h3 {
        color: white !important;
        text-shadow: 2px 2px black !important;
    }

    div.info-card .text-secondary {
        color: rgb(155, 165, 180) !important;
        text-shadow: 1px 1px black !important;
    }
    /* Feature cards */
    div.feature-card h3 {
        text-shadow: 2px 2px black !important;
    }

    div.feature-card small {
        color: rgb(155, 165, 180) !important;
        text-shadow: 1px 1px black !important;
    }

    div.feature-card .feature-icon-container {
        background-color: transparent !important;
    }

    div.feature-card .feature-icon {
        border-color: var(--feature-icon-border-color) !important;
        border-width: 3px !important;
        box-shadow: var(--feature-icon-color) 0px 0px 10px 0px inset, var(--feature-icon-color) 0px 0px 10px 0px !important;
    }
    /* Next pages */
    .card-arrow .text-primary-highlight {
        color: var(--card-arrow-color) !important;
    }

    .main-content .container .row h2 {
        color: white !important;
    }
    /* =========== Connected =========== */
    .btn.btn-light.active, .btn.btn-light:focus, .btn.btn-light.focus, .btn.btn-light:hover, .btn.btn-light:not(:disabled):not(.disabled):active, .btn.btn-light:not(:disabled):not(.disabled).active {
        background-color: var(--button-color) !important;
        border-color: var(--button-border-color) !important;
    }
    /* MEX stats */
    .modal-content .card-footer .link-style:hover {
        color: var(--anchor-hover-color) !important;
    }
    /* Account details */
    .btn-dark:hover {
        background-color: var(--button-hover-color) !important;
    }

    .btn-close-link:hover {
        color: var(--button-hover-color) !important;
    }

    .card-body a.link-style:hover {
        color: var(--anchor-hover-color) !important;
    }
    /* Connect to a wallet pop-up */
    .modal-card .modal-card-body a:hover {
        color: var(--anchor-hover-color) !important;
    }

    .modal-card .modal-card-body .unlock-page .btn-unlock {
        border: 2px solid var(--button-hover-border-color) !important;
    }

    .modal-card .modal-card-body .unlock-page .btn-unlock:hover {
        color: var(--button-hover-color) !important;
    }

    /* Other SVG logos */
    div.unlock-page svg.elrond-symbol path {
        fill: var(--logo-color) !important;
    }

    @-webkit-keyframes rotating {
        from {
            -webkit-transform: rotate(0deg);
        }
        to {
            -webkit-transform: rotate(360deg);
        }
    }

    div.token-symbol svg path {
        fill: var(--logo-color) !important;
    }

    div.token-symbol {
        background-color: white !important;
        -webkit-animation: rotating 5s linear infinite;
    }

    div.token-symbol:hover {
        -webkit-animation: rotating 2s linear infinite;
    }

    .bg-primary {
        background-color: var(--logo-color) !important;
    }
    /* Loading spinner */
    .fa-spin {
        color: var(--spinner-color) !important;
    }

    /* Others from the first page */
    /* Lightning symbol */
    .hero-lightning {
        background-image: url('https://media.giphy.com/media/8L1Ln4gOOabZIz0Q0s/giphy.gif') !important;
    }

    /* Main title */
    .title-holder h1 {
        color: yellow !important;
    }

}
