style.css:
/* i really want this to be global */
 ::-webkit-scrollbar {
    display: none;
}
html {
    background: #f3f3f5;
    transition: all .5s ease-in-out;
}
.cu2-views-bar__options {
    min-height: 40px;
    padding: 0 17px 0 0;
}
.cu-data-view-item_tab.cu-data-view-item_selected {
    background: #f3f3f5;
}
.cu-panel-board.cu-panel-board_caption {
    border-radius: 0 0 8px 8px;
}
.cu-panel-board {
    box-shadow: none;
    border-radius: 8px;
}
.cu-dashboard-board-card__task {
    border: none;
    border-radius: 8px;
}
.cu-panel-board__clickable-name {
    line-height: 1.4;
}
.cu-panel-board.add-task .add-task__link {
    margin-top: 4px;
}
.due-date__info-value {
    color: #838383;
}
.cu-panel-board .cu-dashboard-board-card__features {
    position: relative;
    display: flex;
    margin-left: 0;
    border-radius: 0 0 8px 8px;
}
.cu-panel-board:hover .cu-dashboard-board-card__features {
    display: flex;
    box-shadow: none;
}
.done-task-btn__icon, .cu-panel-board__quick-action-ellipsis {
    stroke: #d6d8e3;
    fill: #d6d8e3;
}
.cu-dashboard-board-card__task:hover, .cu-dashboard-board-card__task_dropdown-opened, .cu-panel-board_hover-state .cu-dashboard-board-card__task {
    box-shadow: 0 2px 8px rgb(47, 49, 66, 16%);
}
.cu-dashboard-board__column {
    padding-top: 1px;
    width: 280px;
}
.cu-panel-board__column-body:hover {
    border-radius: 12px;
    background: #eeeef1;
}
.cu-panel-board__column-drag {
    padding-right: 6px;
}
.cu-dashboard-board-card__task .cu-subtasks-by-status-popup_no-subtasks, .cu-dashboard-board-card__task_dropdown-opened .cu-subtasks-by-status-popup_no-subtasks, .cu-panel-board_hover-state .cu-dashboard-board-card__task .cu-subtasks-by-status-popup_no-subtasks {
    display: inline-block;
}
.cu-panel-board__footer_left .cu-points-estimates, .cu-panel-board__footer_left .cu-recurring-date-dropdown, .cu-panel-board__footer_left .time-estimates-view {
    margin-top: 4px;
    margin-left: 8px;
}
.cu-panel-board__footer_left .cu-points-estimates, .cu-panel-board__footer_left .cu-recurring-date-dropdown, .cu-panel-board__footer_left .time-estimates-view {
    max-height: 20px;
}
.cu-dashboard-board-card .cu-task-row-status_border {
    margin: 4px 0 4px 4px;
}
.cu-task-row-status_border .cu-dropdown {
    border-radius: 0px;
}

.cu-task-row-status_border .cu-dropdown:hover {
    min-width:0;
}

.cu-task-row-status_border:after, .cu-task-row-status_border:before {
    display: none;
}

.cu-task-row-status_border:after {
    margin-top: 4px;
    height: 90%;
    width: 0;
}
.cu-task-row-status_border .cu-task-row-status__item {
    width: 16px;
    height: 16px;
    padding-right: 4px;
    border-radius: 50%
}
.cu-task-row-status_border .cu-task-row-status__item:before {
    display: none;
}
