style.css:
@-moz-document domain("rumble.com") {
html,body{

    background: #113452;
    color: #fff;
}
:root {
    scrollbar-color: #ffd201 #15202b;
}


/*HEADER*/
/*Hide orginal logo*/

.header-logo img {

   FONT-WEIGHT: 200;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-image: url("https://svgur.com/i/beW.svg");
    background-repeat: no-repeat;
    width: 10%;
    /* Width of new image */
    height: 30px;
    /* Height of new image */
padding-left: 600px;
    /* Equal to width of new image */
}  


/* Grad on chat removal*/
.chat--history-list-shadow {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1rem;
    content: "";
    background: linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0) 100%);
}


.chat--header {
    margin: 0 .5rem .75rem .5rem;
    font-size: 1.5rem;
    
    color: #ffd201;
    text-transform: uppercase;
}


    
    /* red dot in title */
    .media-heading-info::before {
	content: "\00B7";
	padding: 0 8px 0 2px;
	font-weight: bold;
	color: #ffd201;
	display: inline-block;
}
    
    /* verified check */
    .verification-badge-icon {
	color: #000;
	fill: #DFD019;
}
    
    
    /* add comments to whole video */
    .comments-create-textarea {
	box-sizing: border-box;
	width: 100%;
	height: 9rem;
	min-height: 3.5rem;
	border: 1px solid #ccc;
	border-radius: 1rem;
	padding: 1rem;
	outline: none;
	background: #253341;
	resize: vertical;
}
    
    
    
    
    
    
    
    
    
    
    
    
    /*   Live count of people watching    */
    
    .watching-now {
	margin: 1rem;
	font-size: 1.0rem;
        font-weight: 600;
	color: #ffd201;
}
    
    .chat-history--username {
  font-weight: bold;
  display: inline-block;
        	color: #ffd201;
  margin-right: 0.5em;
}
    
/* comment authors name */
    .comments-meta-author {
        color: #ffd201;
	font-weight: bold;
}
    
    
    
    
.header-search-field {
    
}

.header-search-select option {
    color: #ffd201;
    
}

.header-upload {
    box-sizing: border-box;
    border-radius: 10rem;
    padding: 0 0.35rem;
    background: #ffd201;
    height: 2.6rem;
    width: 2.6rem;
    margin: 0 1rem;
    display: flex;
    align-items: center;
}

input,
textarea,
select {

    color: #ffd201 !important;
    border-color: #ffd201 !important;
}

.header-search {
    display: flex;
   
    align-items: center;
    border-width: 2px;
    border-radius: 8rem;
    background: #113452;
}

.header-search-field {
    -webkit-appearance: textfield;
    outline-offset: -2px;
    border-left: 1px solid #bbb;
    background: #113452;
    width: 100%;
}


.header-search-icon {
    stroke: #ffd201 !important;
    fill: #ffd201;
}

.header-search-select,
.header-search-field {
    background: #113452;
}


.header-search-select {
    -moz-appearance: none;
    background: #113452;
    color: #d03bc8;
    text-indent: 0.01px;
    text-overflow: ellipsis;
}




/*HEADER End*/
/*Main Menus*/
.hover-menu {
    position: absolute;
    color: #ffd201;
    background: #021c3c;
    box-shadow: 0 0 20px 0 rgba(0, 0, 0, 0.8);
    width: 16rem;
    padding: 0 1rem .2rem 1rem;
    font-size: 1.15rem;
    box-sizing: border-box;
    max-height: calc(100vh - 4rem);
    overflow: auto;
}

.main-menu-icon {
    width: 1.8rem;
    height: 1.8rem;
    margin: 0 0.5rem -0.5rem 0;
    stroke: #ffd201;
    stroke-width: 2px;
}





/*login form*/
.overlay-heading {
    background: #253341;
    width: 100%;
    color: #ffd201;
    font-size: 1.6rem;
    padding: 0.6rem;
    box-sizing: border-box;
    white-space: nowrap;
    display: flex;
    justify-content: space-between;
    align-items: center;
}



.overlay {
    background: #253341;
    position: relative;
    box-shadow: 0 0 20px 0 rgba(0, 0, 0, 0.8);
}


.login-form {
    text-align: center;
}

.login-form-login-div {
    max-width: 30rem;
    margin-left: auto;
    margin-right: auto;
}

.login-form-register-div {
    border-top: 1px solid #ddd;
    background: #253341;
    padding: 1.5rem 1.5rem 2rem;
    box-sizing: border-box;
    font-size: 1.5rem;
    /* margin-top: 2rem; */
}



.login-form-input {
    width: 100%;


    outline: none;
    padding: 10px;
    box-sizing: border-box;
    margin-bottom: 1rem;
}


input,
textarea,
select {
    color: #ffd201 !important;
    background: #253341;

    border-color: #ffd201 !important;
}



/*Main Menus End*/
/*Right menu Tabs*/
.tab-heading.tab-active {
    background: #021c3c;
    border: 1px solid #ffd201;
    border-bottom: 0;
    z-index: 10;
}



.listing-header--letter:before {
    display: inline-block;
    vertical-align: middle;
    border-radius: 50%;
    border: 2px solid #eee;
    width: 56px;
    height: 56px;
    text-align: center;
    font-size: 48px;
    line-height: 56px;
    font-weight: bold;
    content: "E";
    background-color: #ffd201;
    color: white;
    text-transform: capitalize;
}

/*End Right Menu Tabs*/
/*MAIN-PAGE*/
.module-title {
    color: #ffd201;
}
.container {
    background: #021c3c;
    border-color: #ffd201;
    border-radius: 2% 2% 2% 2%;
}

.mediaList-item {
    padding: 1rem 0;
    border-bottom: 1px solid #ffd201;
}


/*Below Specificaly Affects Right Video List Allowing No rounded left top border*/
.tabs > .mediaList-list {
    box-sizing: border-box;
    border-radius: 0px 2% 2% 2%;
}

.mediaList-list.related {
    max-width: 500px;
    margin: 0 auto;
    border-radius: 0px 0% 0% 0%;
}

.mediaList-link-more a,
.promo-widget-link {
    background: #021c3c;
    border-color: #ffd201;
    color: #ffd201;
}
.mediaList-link-more a:hover {
     background: #113452;
     border-color: #ffd201;
    color: #ffd201;
}

.header-user-name {
    color: #ffd201;
}

.main-menu-close,
.main-menu-open {
    stroke: #fff;
    stroke-width: 2px;
}


/*amount earned*/
.mediaList-rumbles,
.mediaList-earnings,
.mediaList-liveCount {
    background: #15202b;
    border-color: #ffd201;
    color: #ffd201;
}
.mediaList-earnings a:hover {
    color: #15202b;
}


/* Bottom offers content */
.promo-widget-content {
    color: #fff;
    padding: 1rem 2rem;
    background:none;
    display: flex;
    flex-direction: column;
    height: 100%;
    justify-content: space-between;
}

.promo-widget-broadcasters .promo-widget-content {
    background: none;
}

.promo-widget-creators .promo-widget-content {
    background: none;
}

/* End Bottom offers content */



/*Media earnings on actual media page */
.media-earnings {
    color: #ffd201;
    font-weight: bold;
    clear: left;
    display: block;
}

/* Up and Down Vote */
.rumbles-vote-up {
    background: #ffd201 !important;
}

.rumbles-vote-down {
    background: #113452 !important;
}


.rumbles-vote-up,
.rumbles-vote-down {
    float: left;
    display: inline-block;
    padding: 5px 8px;
    border: 1px solid #fff;
    background: #fff;
    line-height: 0;

    color: #fff;
}




.mediaList-by-heading,
.forum-post-meta,
.promo-widget-link:hover,
small {
    color: #fff;
    text-decoration: none;
}



/*MAIN-PAGE*/
/*ARTICLE-VIDEO*/
.rumbles-vote,
.comment-count {
    color: #ffd201;
}
.rumbles-vote-up {
    background: green;
}
.rumbles-vote-down {
    background: #a80202;
}
.media-video-action {
    background: #15202b;
    border: 1px solid #ffd201;
}
.media-icon-license,
.media-icon-embed,
svg.user-image--icon {
    stroke: #ffd201;
}
svg.user-image--icon {
    background: #021c3c;
    border-color: #ffd201;
}
.round-button {
    background: #021c3c;
    border: 1px solid #ffd201;
    color: #ffd201;
}
.round-button:hover {
    background: #ffd201;
    color: #021c3c;
}
/*ARTICLE-VIDEO*/
/*Account Overview*/
#-v2 h1 {
    color: #f4f4f4;
    font-size: 1.83em;
    font-weight: 500;
    padding-bottom: 0.5em;
    text-transform: uppercase;
}

#-v2 a.green {
    color: #ffd201;
}

#-v2 .green-box {
    background: #ffd201;
}


#-v2 .blue-box {
    background: #021c3c;
}


#-v2 .cash-out a {
    float: right;
    display: block;
    background: #ffd201;
    width: 280px;
    padding: 20px 0;
    margin-top: 10px;
    text-align: center;
    color: #fff;
    font-weight: 300;
    font-size: 40px;
    text-transform: uppercase;
}

#-v2 .cash-out a:hover {
    background: #253341;
    color: #ffd201;
    border: 2px solid #fff;
    border-color: #fff;
}


#-v2 .statsa input {
    width: 95%;
}

#-v2 .statsa input, #-v2 .statsa select {
   
    background-position: calc(100% - 20px) 20px;
    line-height: 1;
    border: 0;
    padding: 0 2.5%;
    height: 45px;
    width: 100%;
    text-indent: 5px;
    -webkit-appearance: none;
    color: #fff !important;
    margin-bottom: 10px;
    box-shadow: inset 0 0 1px #333;
    font-size: 16px;
}

#-v2 input, #-v2 select {
    vertical-align: middle;
}


#-v2 h2 {
    color: #fff;
    font-size: 1.8em;
    font-weight: 500;
    padding-bottom: 0.8em;
}

#-v2 .statsa #cmessage {
    display: block;
    text-align: left;
    margin: 0 0 8px 0;
    font-size: 14px;
    font-style: italic;
    color: #fff;
}

#-v2 .vOwn a, #-v2 .vAll a {
    color: #ffd201;
    border-bottom: 0px solid rgba(0,0,68,0.2);
    line-height: 130%;
}

#-v2 .statsa #dchart a, #-v2 .vNon a {
    color: #fff;
    border-bottom: 0px solid rgba(0,51,0,0.2);
    line-height: 130%;
}

#-v2 .google-visualization-table-table .google-visualization-table-th {
    /* background: #c0c5ce; */
    padding: 18px 10px;
    border: 0;
    /* border-bottom: 3px solid #75a642; */
    font-size: 16px;
    background-image: none !important;
}

#-v2 .right-box-wrap {
    border: solid 1px #ffd201;
    background: #021c3c;
    box-sizing: border-box;
    margin-bottom: 20px;
}

#-v2 .user-info h3 {
    font-size: 20px;
    color: #f4f4f4;
    font-weight: 300;
}

#-v2 .side-link-nav li a.active {
    background: #ffd201;
    position: relative;
}

#-v2 .side-link-nav li a {
    padding: 10px 15px;
    display: block;
    color: #f4f4f4;
}

#-v2 .side-link-nav li a:hover {
    background: #ffd201;
    color: #f4f4f4;
}

#-v2 h5 {
    color: #f4f4f4;
    font-size: 1em;
    font-weight: 300;
    text-transform: uppercase;
    padding-bottom: 1em;
}


#-v2 .button-small, #-v2 .video-tags a, #-v2 .secondary-action, #-v2 .overlay-dialog .buttons a {
    background: #15202b;
    color: #fff;
    padding: 0 10px;
    outline: none;
    border: 1px !important;
    cursor: pointer;
    height: 32px;
    line-height: 32px;
    font-size: 14px;
    font-weight: 300;
    display: block;
}




/*End account Overview*/
/*my subscriptions*/
.listing-header--white-bg {
    background: #113452 !important;
}

.video-item,
.tuner-box--section,
.paginator--link {
    background: #021c3c !important;
    border: 1px solid #ffd201 !important;
}

.video-item {
    position: relative;
    height: 155px;
    background-color: white;
    box-shadow: 0 0px 0px 0 #000;
}


/*HELP*/
.book-body,
#book-search-input {
    background: #113452;
    color: #ffd201;
}
.markdown-section h1,
.markdown-section h2,
.book-summary ul.summary li a,
#book-search-results .search-results .has-results .search-results-item a {
    color: #ffd201;
}
.markdown-section,
.book-summary ul.summary li ul li a,
#book-search-results .search-results .has-results .search-results-item {
    color: #fff;
}
.book-summary {
    background: #113452;
    border-right: 1px solid #ffd201;
}

.book .book-summary .book-logo {
  
    background: none;
      -moz-box-sizing: border-box;
    box-sizing: border-box;
     background-image: url("https://svgur.com/i/beW.svg");
    background-repeat: no-repeat;
      width: 10%;
    /* Width of new image */
    height: 30px;
    /* Height of new image */
    text-align: center;
    padding: 5px;
    padding-left: 600px;
    
       
}

.book.with-summary .book-summary {
    left: 0;
     padding: 8px;
}



.markdown-section table thead tr,
.markdown-section table tbody td {
    background: #f04cc3;
}
.markdown-section table thead th {
    color: #5ae1cc;
}
.markdown-section code {
    background: #a80202;
    color: purple
}
.markdown-section pre {
    background: #15202b;
    border: 1px solid #ffd201;
}
/*HELP*/
/*CORPORATE*/
.header {
    background: #021c3c;
    border-bottom: 1px solid #ffd201;
}
.header .rumble-logo {
    background: #fff;
    border: 2px solid #ffd201;
}
.header .main-nav a,
.corp-section .section-titles h3,
.solution-lineup .third-section h4,
.brand-box .stats .stats-wrapin .big {
    color: #15202b;
}
.header .main-nav a:hover,
.brand-box .content p,
.brand-box .stats .stats-wrapin .small {
    color: #fff;
}
.solution-lineup,
.solution-lineup .check-list .checkmark,
.brand-box {
    background: #15202b;
}
.solution-lineup .check-list .container:hover input ~ .checkmark {
    background: #ffd201;
}
.timeliny .timeliny-dot::after {
    background: #15202b;
    border: 1px solid #ffd201;
    color: #fff;
}
/*CORPORATE*/
/*VIDEO*/
.listing-header--white-bg {
    background: #b77979;
}

.video-item,
.tuner-box--section,
.paginator--link {
    background: #64e877;
    border: 1px solid #ffd201 !important;
}
.video-item:hover {
    background: #ffd201;
}
.tuner-box--section-header {
    color: #ffd201;
}
.tuner-box--link--current,
a.tuner-box--link:hover,
.paginator--link--current,
.paginator--link:hover {
    background: #ffd201;
    color: #fff !important;
}


.tuner-box--link {
    color: #fff;
}
.paginator--ul {
    color: #fff;
    border-radius: 0;
    box-shadow: 0 0px 0px 0 #000;
}

.tuner-box--section {
    box-shadow: 0 0px 0px 0 #ccc;
    background: #000;
    margin-bottom: 1px;
}

i.user-image--letter {
    display: inline-block;
    box-sizing: border-box;
    border: 1px solid #eee;
    border-radius: 50%;
    background-color: #ffd201;
}

#-v2 .product-entry-title, .product-entry-title {
    display: flex;
    flex-shrink: 0;
    align-items: center;
    background: #021c3c;
    color: #fff;
    font-size: 2rem;
    line-height: 1.25;
    padding: 1rem 2rem;
}

#-v2 .product-entry, .product-entry {
    position: relative;
    background: #021c3c;
    display: flex;
    flex-direction: column;
}

#-v2 .plan-price, .plan-price {
    color: #ffd201;
    font-size: 1.6em;
    font-weight: 700;
}

#-v2 .plan-features li, .plan-features li {
    font-size: 1.15em;
    margin: .6em 0;
    color: #ddd;
}

.bg-green {
    background: #ffd201;
    color: #fff;
}

#-v2 .upload-video-page h2 {
    padding: 0 0 20px;
     color: #ffd201;
}



/*VIDEO*/
/*TERMS*/
.legal-nav--a {
    background: #15202b;
    border: 1px solid #ffd201;
}
.legal-nav--a:hover {
    color: #15202b;
}

.book-summary ul.summary li.active>a {
    color: #ffd201;
    background: 0 0;
    text-decoration: none;
}

/*TERMS*/
/*SUBPAGES*/
/*FOOTER*/

.footer {
    clear: both;
    background: #113452;
    color: #ddd;
    padding-top: 3rem;
    padding-bottom: 3rem;
}


.footer-terms:after {
    content: " Rumble Here Theme By ZmAn3";
    margin-left: 1rem;
    color: #ffd201;
    cursor: none;
}
/*FOOTER*/
}
