style.css:
@-moz-document domain("nitrotype.com") {
.pop--primary:before {
   color: #00ff04;
   background: #00eaff;
}
.daily-challenge-progress.is-complete .daily-challenge-progress--progress-bar {
   background: #ff59e9;
}
.daily-challenge-progress--name {
   color: #cbff00;
   position: relative;
   max-width: 90%;
}
.season-progress-widget--level-progress-bar {
   border-radius: 100px;
   background: #00ffa4;
   height: 100%;
}
.daily-challenge-progress--badge {
   width: 40px;
   display: flex;
   flex-wrap: wrap;
   align-items: center;
   justify-content: center;
   position: relative;
   left: 4px;
   color: blue;
}

.profile--active-car--car-name {
   position: absolute;
   top: 100%;
   left: 50%;
   background: #0028ff;
   border-radius: 5px;
   padding: 10px 20px;
   color: #ff0202;
   opacity: 0;
   transition: all 0.3s cubic-bezier(0.55, 1.67, 0.57, 0.87);
   -webkit-user-select: none;
   -moz-user-select: none;
   -ms-user-select: none;
   user-select: none;
   text-align: center;
   white-space: nowrap;
}
.season-progress-widget {
   display: grid;
   gap: 15px;
   grid-template-columns: 1fr auto;
   color: #d6ff00;
}

.profile-goldNotice {
   background: fixed;
   padding: 10px 15px;
   color: rgb(255, 247, 14);
   font-weight: 600;
   font-size: 14px;
   line-height: 1.33em;
   border-radius: 5px;
        -webkit-animation: colorchange 10s infinite alternate;
}
@-webkit-keyframes colorchange {
    0% {
        color: #f00;
    }
    10% {
        color: #ff5e00
    }
    20% {
        color: #fbff00
    }
    30% {
        color: #73ff00
    }
    40% {
        color: #00ff8c
    }
    50% {
        color: #00fffb
    }
    60% {
        color: #0400ff
    }
    70% {
        color: #f700ff
    }
    80% {
        color: #ff00ae
    }
    90% {
        color: #ff007b
    }
    100% {
        color: red
    }
}

.icon-help-s {
    width: 135px;
    height: 13px;
}


.mysteryBox-boxImg {
    content: url("https://i.ibb.co/mC0FZyj/157930039635295750-60807.png");
}
.profile.is-gold .mysteryBox-copy {
    background: 50% no-repeat, linear-gradient(180deg, rgb(251, 2, 255) 0, #f00 30%, #ffbc00 80%, #f9ff00);
}

path[fill="#D62F3A"] {
    fill: #02f;
}
.growl--a {
    background: fixed url(https://wallpapercave.com/wp/wp4871139.jpg);
    background-size: 20%;
    border-color: rgb(221, 0, 255);
    color: #00ff15;
}
.growl {
    -webkit-animation: slide-in-left .2s cubic-bezier(.25, .46, .45, .94) both;
    animation: slide-in-left .2s cubic-bezier(.25, .46, .45, .94) both;
    border-radius: 5px;
    border-style: solid;
    border-width: 1px 1px 3px;
    box-shadow: 0 28px 28px 0 rgb(38 243 0), 0 17px 17px 0 rgb(0 161 255), 0 8px 8px 0 rgb(255 194 0);
    flex: 0 0 auto;
    margin-bottom: 15px;
    order: 2;
    padding: 10px;
    position: relative;
    width: 100%;
}
.growl-close {
    background: #00ff0b;
    border-radius: 50%;
    border: 1px solid #005aff;
    color: #ffe100;
    padding: 10px;
    position: absolute;
    right: -20px;
    top: -5px;
    transition: all .25s ease-in-out;
}



