/* ==UserStyle==
@name           Dark Animego
@namespace      userstyles.world
@version        1.0.2
==/UserStyle== */
@-moz-document domain("animego.org") {
  body, #wrap {
	background-color: rgb(28, 28, 28) !important;
}
.content, .card {
	background-color: #282828;
	box-shadow: 0 0 10px rgba(0, 0, 0, .7);
	border-radius: 5px;
}
.card-block.p-0 {
	box-shadow: none;
}
.card {
	border: none;
}
.card-header, .card-body.bg-white, .noUi-tooltip, .btn-filter {
	color: #DDDDDD;
	background-color: #3e3e3e75 !important;
}
.card-header.w-100 {
	background-color: #ffffff17;
}
  .my-list .planned {
  color: #d18c0f;
  border:none;
  }
.font-weight-600 {
    color: #f1f1f1;
}
.list-group-item-action {
  color: lightcyan;
}
.list-group-item-action:focus, .list-group-item-action:hover {
    color: #16181a;
    text-decoration: none;
    background-color: #fdfeff3d;
}
.list-group-item, .dropdown-menu.show {
	background-color: #3e3e3e75;
}
.read-more-text {
   color:#ddd
}
.read-more-container[data-read-more-init=true]::after {
   background-image:none;
}
.search-form {
background: rgb(0 0 0 / 20%);
box-shadow: 0 1px 5px rgb(0 0 0 / 60%);
}
.form-control {
	border: none;
	background-color: #4e4e4e;
	color: #ddd;
}
.form-control:focus {
	background-color: #6e6e6e;
	color: #ddd;
}
.video-item, .screenshots-item {
	box-shadow: 0 0 25px 0px rgb(0, 0, 0)
}
.animes-grid-item-body {
	padding-left: 10px !important;
	padding-right: 5px !important;
}
.anime-grid-item {
	padding-left: 5px;
	padding-right: 5px;
}
.custom-control-description.b-tooltipped {
	color: white;
}
.dropdown-item:focus, .dropdown-item:hover {
	background-color: #00000066;
	color: #ff5c57;
}
.btn-filter:focus {
	color: #dc3545;
}
.read-more-container {
	box-shadow: none;
}
.anime-popover, .popover-body {
	background-color: #323232;
	color: #e9ebee;
}
.searchsuggestions {
	color: #ddd;
	background-color: #212529
}
/*Texts*/
.text-left, .text-gray-dark-6, .container, .text-link-gray, .card, .b-tooltipped, .text-gray-dark-8 {
	color: #DDDDDD;
}
.text-dark {
color: #f07860 !important;
}
a.text-dark:focus, a.text-dark:hover {
    color: #ffc8bd!important;
}  
.text-gray-dark-5, .text-gray-dark-4, .list-group-item, .btn-o-4 {
	color: #e5f2ff;
}
a {
	color: #f47961;
}
@media (min-width: 992px) {
	.comments .children {
		margin-left: 40px;
	}
}
@media (max-width: 991px) {
	.comments .children {
		padding-left: 40px;
		border-left: 2px solid #ffdfc0;
	}
}
/*SCROLL BAR*/
::-webkit-scrollbar {
	width: 4px;
	height: 5px;
	background-color: #684332;
	border-radius: 1em;
}
::-webkit-scrollbar-thumb {
	background-color: #914c0e;
	border-radius: 1em;
	box-shadow: inset 1px 1px 10px #ffdfc0;
}
}
