style.css:
@-moz-document domain("dramacool9.co"), domain("dramacool9.io"), domain("www13.watchasian.co"), domain("dramacool.movie"), domain("dramacool.sk") {
html, body {
    background-color: #282A36 !important;
    background: #000;
    font-family:"Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 12px;
    color: white;
}
.logo {
    height: 73px;
    width: 970px;
    background: url(https://i.imgur.com/kijjA7F.jpg) left top no-repeat !important;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    display: block;
}
.ads-evt {
    opacity: 0;
}
.container {
    background: #44475A;
}
.block {
    background: #44475A;
}
.anime_muti_link {
    color: #BD93F9;
}
.anime_muti_link ul li:nth-child(2n+1) {
    background-color: #6272A4;
}
.list-episode-item-2 li:hover, .list-episode-item-2 li:nth-child(even) {
    background: #6272A4;
}
.content-left {
    width: 100%;
}
.content-right {
    display: none;
}
.block, .ads_place {
    border: none;
}
.plugins2 {
    margin-top: -2px;
}
.plugins2 .direction a {
    color: #ffffff;
    background: #6272A4;
}
.plugins2 .direction select, .plugins2 .direction select option {
    background: #BD93F9;
    color: #fff;
    -webkit-appearance: none;
    border-radius: 6px;
    margin-right: 0px;
    margin-left: 2px;
    padding: 6px;
}
.show-all {
    position: absolute;
    right: 22px;
    top: 5px;
    cursor: pointer;
    color: #BD93F9;
}
.tab li:hover, .tab li.selected {
    background: #FF79C6;
    margin-left: 10px;
}
.search {
    height: 34px;
    overflow: hidden;
    position: absolute;
    top: 50px;
    right: 0px;
    z-index: 10;
}
.char-list {
    background: #282a36;
    font-size: 0;
}
.char-list li {
    display: inline-block;
    border-right: 1px solid #6272A4;
}
.switch-view {
    position: absolute;
    right: 0;
    top: 5px;
    padding-right: 10px;
}
.list-episode-item-2 .title {
    float: left;
    font-weight: normal;
    padding: 5px;
    font-size: 12px;
    color: #899596;
}
.tab li {
    display: inline-block;
    background: #BD93F9;
    color: #FFF;
    padding: 5px 10px;
    cursor: pointer;
    margin-right: -1px;
}
.tab li:hover, .tab li.selected {
    background: #FF79C6;
    margin-left: 0px;
}
element.style {
    background: #BD93F9;
    background-color: rgb(189, 147, 249);
}
.navbar {
    background: #FF79C6;
    font-size: 0;
}
.navbar > li > a:hover {
    font-size: 12px;
    font-weight: 700;
    float: left;
    background: #BD93F9;
}
.menu_m {
    background-color: #BD93F9;
}
.details .info .other_name a {
    color: #BD93F9;
}
.details .info span {
    color: #BD93F9;
}
.details .info .other_name a:not(:last-child):after {
    content: '/ ';
    color: #FF79C6;
}
.details .info a {
    color: #f8f8f2;
}
.follow {
    background-color:#6272A4;
}
.search select {
    text-shadow: 0 0 0 #BD93F9;
    background: #44475A;
}
.slider-star .slick-prev, .slider-star .slick-next {
    background:#BD93F9;
}
.tags > a {
    color: #BD93F9;
}
.tags > a:hover {
    color: #FF79C6;
}
div.btn-comment {
    background:#BD93F9;
}
div.btn-comment:hover {
    background:#FF79C6;
}
.list-episode-item-2 .type.sub, .list-episode-item-2 .type.SUB {
    background:#BD93F9;
}
.all-episode li .title {
    color:#BD93F9;
}
.all-episode li .title:hover {
    color:#FF79C6;
}
.btn-btt {
    background:#BD93F9;
}
.list-popular li a {
    color: #BD93F9;
}.list-popular li a:hover {
    color: #FF79C6;
}
.view-more a {
    color: #BD93F9;
}
.search button {
    background:#BD93F9; 
}
.search { 
    background: #282a36;
}
.search input {
    background: #44475A;
}
input {
    color: #f8f8f2;
}
.ui-autocomplete {
    background: #44475A !important;
    color:#f8f8f2;
}
footer {
    background:#BD93F9;
    color:#f8f8f2;
}
.pagination li a {
    color: #FF79C6;
}
.pagination li a:hover {
    background: #BD93F9;
}
.pagination li.selected a, .pagination li a:hover {
    background: #BD93F9;
}
.watch-drama .category a {
    color:#BD93F9;
}
.watch-drama .category a:hover {
    color:#FF79C6;
}
.watch-drama h1 {
    color: #F1FA8C;
}
.note a {
    color:#BD93F9;
}
.note a:hover {
    color:#FF79C6;
}
.anime_muti_link ul li:hover span, .anime_muti_link ul li.selected span {
    background: #F1FA8C;
}
div.element.style {
    color:#BD93F9;
    border-bottom:1px solid #BD93F9;
}
}
