style.css:
@-moz-document domain(astatalk.com) {
html, body {
background-image:url(http://i34.tinypic.com/4jl011.jpg) !important;}

DIV.topline, DIV.c:first-child {
background-image:url(http://i37.tinypic.com/2is7fx5.png) !important;}

DIV.c:first-child *, TD.search {
font-size:10px !important;
text-decoration:none !Important;}

PRE.ascii-logo {
background-image:url(http://i34.tinypic.com/15qp1g4.png) !important;
background-repeat:no-repeat !important;
background-position:center top !important;
Font-size:0px !important;}

P.download.textbody a {
text-decoration:none !important;}

SCRIPT[language="JavaScript"] + DIV#content.c > DIV.title > H1, DIV#content, P.textbody, TD.release, DIV.title, #content a, big {
font-size:11px !important;}

SPAN.c_title, SPAN.asta_title, #content th span {
background-image:url(http://i37.tinypic.com/b8s3s2.png) !important;
-moz-border-radius:4px !important;}

#content h2 {
font-size:14px !important;}

INPUT.search-line {
-moz-border-radius:4px !Important;
opacity:.7 !important;
background-image:url(http://i37.tinypic.com/69p1ef.png) !Important;}

INPUT.search-line:hover {
opacity:.85 !important;}

INPUT.search-line:focus {
opacity:1.0 !important;}

DIV.c:first-child a:hover, P.download.textbody a:hover {
color:#DEDEDE !important;}

P.download.textbody {
-moz-border-radius:4px !important;
border:1px solid !Important;
border-color:transparent !important;}

P.download.textbody:hover {
border-color:#CCC !important;}

INPUT[name="category[]"] {
-moz-appearance:none !important;
-moz-border-radius:3px !important;}

* {
font-family:Segoe UI !important;}
}
