@-moz-document domain("duolingo.com") {
/* Author: Keven Alves */
/* Project Web site: https://nightlingo.netlify.app/       */

:root{

    --colorBackground: #0B0E14;
    --colorBorder: #171E2B;
    --userColor: #0E1F2B;
    --hover: #131924;
    
    --scroll: #181818;
    --scroll-overlay-light: #1c1c1c20;
	--scroll-overlay-medium: #1c1c1c40;
	--scroll-overlay-heavy: #1c1c1c80;
    
    --color-textLeasson: #22a9c7;
    --color-title: #ebede3;
    --color-text: #bfbfbf;
    
}

/* Scrollbar */
html {
  scrollbar-color: #fff4 var(--scroll)
}

._2jbOb ._2-Lx6,
._3P4YK {
  scrollbar-width: thin
}

::-webkit-scrollbar {
	display: block;
	background: var(--scroll);
	background-image: url(data:image/svg+xml;base64,), url(data:image/svg+xml;base64,);
	background-position: top center, bottom center;
	background-size: contain;
	background-repeat: no-repeat
}


/* Track */
::-webkit-scrollbar-track {
	background: var(--scroll);
	border: none
}


/* Thumb */
::-webkit-scrollbar-thumb {
	background: #ffffff40 content-box;
	border: 2px solid transparent
}

::-webkit-scrollbar-thumb:hover {
	background-color: #fff5
}

::-webkit-scrollbar-thumb:active {
	background-color: #fff6
}

/* Buttons Scroll */
::-webkit-scrollbar-button:single-button {
  display: block;
  height: 16px;
  width: 16px;
	background: var(--scroll-overlay-heavy)
}

::-webkit-scrollbar-button:single-button:hover {
	background: var(--scroll-overlay-medium)
}

::-webkit-scrollbar-button:single-button:active {
	background: var(--scroll-overlay-light)
}




