style.css:
@-moz-document url("https://www.baidu.com/") {
/* 隐藏百度图标 */
.s-p-top {
    display: none;
}
/* 设置背景图片 */
body {
    background:url(https://i.loli.net/2020/06/13/oLxPXIeSZGyjEvM.png) fixed;
    background-size: cover;
}
/* 设置滚动栏不可见 */
body::-webkit-scrollbar {
    display: none;
}
/* 设置input输入框的placeholder属性 */
#soutu-url-kw::-webkit-input-placeholder {
    color: #ddd !important;
}
/* 添加一层黑色并设置透明度，使图片亮度低一些 */
#wrapper {
    background-color: #00000022 !important;
}
/* 设置顶部导航栏被背景透明 */
#s_top_wrap {
    background-color: #ffffff00;
}
/* 设置底部信息栏背景透明 */
#bottom_layer {
    background: #ffffff00;
}
/* 搜索框：透明 */
#head_wrapper #kw, .soutu-btn {
    background-color: #ffffff00 !important;
    color: #dfdfdf;
}
/* 搜索框 */
.s_ipt {
    border-radius: 5px !important;
    border: 2px solid #B4E4A8 !important;
    color: #ddd !important;
    transition: all 0.3s;
}
/* 鼠标经过搜索框 */
#s_kw_wrap:hover #kw {
    box-shadow: 0px 0px 20px #5fd2d1 !important;
    border: 2px solid #B4E4A8 !important;
}
/* 搜索框获取焦点的 */
#head_wrapper #kw:focus {
    background-color: rgba(255, 255, 255, 0.1) !important;
    box-shadow: 0px 0px 20px #5fd2d1 !important;
    border: 2px solid #B4E4A8 !important;
}
/* 百度一下：按钮 */
.btn {
    background-color: #B4E4A8aa !important;
    transition: all 0.3s;
}
/* 百度一下按钮：鼠标经过 */
.btn:hover {
    background-color: #B4E4A8aa !important;
    box-shadow: 0px 0px 20px #5fd2d1 !important;
}
/* 搜索下拉框 */
#head_wrapper #form .bdsug-new {
    margin-top: 7px !important;
    background-color: rgba(255, 255, 255, 0.1) !important;
    border: 2px solid #B4E4A8 !important;
    box-shadow: 0px 0px 20px #5fd2d1 !important;
    border-top: none !important;
}
/* 搜索下拉框：鼠标经过 */
#head_wrapper #form .bdsug-new ul li:hover {
    background: #ffffff11 !important;
    box-shadow: 0px 0px 15px #5fd2d1;
}
/* 符合搜索内容的字体高亮色 */
.bdsug-overflow {
    color: #fff !important
}
/* 搜索下拉框：头部边框 */
#head_wrapper #form .bdsug-new ul {
    border-top: none;
}
/* 搜索下拉框：鼠标经过 */
.bdsug-s {
    color: #0f0 !important;
}
.bdsug-overflow:hover b {
    color: #0f0 !important
}
/* 搜索下拉框：键盘移动 */
 #head_wrapper #form .bdsug-new ul li.bdsug-s {
    background: rgba(100, 220, 250, 0) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1);
}
#head_wrapper #form .bdsug-new ul li b {
    color: #0f0 !important;
}
/* 顶部导航栏：字体颜色 */
.c-font-normal {
    color: #aaa !important;
}
.c-font-normal:hover {
    color: #B4E4A8 !important;
}
.s-bottom-layer-content a:hover, #s-top-left a:hover, .show-weather span:hover {
    color: #B4E4A8 !important;
}
/* 顶部导航栏下拉框 */
.s-top-more, .s-mod-setweather, .c-floating-box {
    background-color: #ffffff11 !important;
}
/* 顶部导航栏下拉框：字体颜色 */
#s-user-name-menu a, #s-user-setting-menu a {
	color: #aaa;
}
#s-user-setting-menu a:hover, #s-user-name-menu a:hover {
    color: #B4E4A8;
}
/* 天气下拉框：每天天气情况 */
.everyday-item:hover {
    background-color: #ffffff33 !important;
}
/* 搜索框距顶距离 */
#s_fm {
    margin-top: 40px !important;
}
/* 搜索框：相机s */
.soutu-btn {
    background: url(data:image/png;base64,) no-repeat !important;
    background-size: 22px !important;
    width: 22px !important;
    transition: all 0.2s;
}
.soutu-btn:hover {
    background: url(data:image/png;base64,) no-repeat !important;
    background-size: 22px !important;
}
/* 图片搜索：百度一下按钮 */
.soutu-env-new .soutu-layer .soutu-url-btn-new {
    background-color: #B4E4A8aa !important;
    transition: all 0.2s !important;
}
.soutu-env-new .soutu-layer .soutu-url-btn-new:hover {
    background-color: #B4E4A8aa !important;
    box-shadow: 0px 0px 10px #5fd2d1 !important;
}
/* 图片搜索框 */
.soutu-env-new .soutu-layer #soutu-url-kw, .soutu-url, .soutu-url-wrap {
    background: #ffffff00 !important;
}
/* 图片搜索下拉框 */
.soutu-env-new .soutu-layer #soutu-url-kw {
    border-radius: 5px !important;
    border: 2px solid #B4E4A8 !important;
    color: #efefef !important;
    transition: all 0.2s
}
/* 图片搜索：传入框 */
.soutu-env-new .soutu-layer .soutu-state-normal {
    background: #B4E4A833 !important;
}
.soutu-env-new .soutu-layer .soutu-drop {
    background: #B4E4A899 !important;
}
.soutu-drop-tip {
    color: #333 !important;
}
/* 图片搜索：上传按钮 */
.soutu-env-new .soutu-layer .upload-wrap {
    background: #B4E4A8 !important;
    color: #333 !important;
}

}
