style.css:
@-moz-document domain("4chan.org"), domain("boards.4channel.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
}

#ft li,

:focus,
div.pagelist,
div.reply,
footer li
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.expandedWebm:hover {
    transform: scale(1.5);
}
/*___ Fonts ________________________________________________________________________*/

#announce .boxbar h2,

.top-box h2,

div.post div.postInfo span.subject
{
	font-size: 2rem!important;
}

#announce .boxcontent,
#arc-list td,
#blotter td,
#ft,
#whatis,

.boxcontent,
.fileText,
.postblock
{
	font-size: 1.85rem!important;
}

#disclaimer-dialog .bd,

.blotter-date,
body,
div#boardNavDesktopFoot a,
div.pagelist
{
	font-size: 1.52rem!important;
}

div#boardNavDesktop,
[data-cmd="x-disc"],
[data-cmd="ok-disc"]
{
	font-size: 1.2rem!important;
}

div.post blockquote.postMessage,
body
{
	line-height: 30px!important;
}

/*___ Remove Items _________________________________________________________________*/

#abc-cnt,
#bannerCnt,

.adc-resp-bg,
.adg-rects,
.middlead.center
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

selector
{
    top: 10px!important;
}

/*___ Margins ______________________________________________________________________*/

#delform,

.c-thread,
.postContainer.replyContainer,
.thread,
.top-box h2,
.top-box,

[name="post"]
{
	margin: 12px!important;
}

/*___ Paddings _____________________________________________________________________*/

#announce .boxbar h2,
#arc-list,
#ctrl,
#delform,

.fileText,
.postContainer.replyContainer,
.thread,
.top-box h2,
.top-box,

[name="post"],
div.reply
{
	padding: 12px!important;
}

#blotter td,

[data-cmd="ok-disc"],
[data-cmd="x-disc"]
{
	padding: 3px 12px!important;
}

/*___ Heights ______________________________________________________________________*/

.c-thread,
.extended-small .thread
{
	max-height: none!important;
}

/*___ Widths _______________________________________________________________________*/

#arc-list,

.fileText
{
	max-width: none!important;
}

#arc-list,
#blotter,
#doc, #doc2, #doc3, #doc4,
#ft ul,

footer
{
	width: 98%!important;
}

#disclaimer-dialog,

.c-thread,
.extended-small .thread
{
    width: 23%!important;
}

}

