@-moz-document domain("4chan.org"), domain("boards.4channel.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

#backdrop {
    background-color: #000000d4;
}

/* Background 
_____________________________________________________________________________________________________*/

#ft,
#hd,

.postblock,

footer
{
	background-color: #060505!important;
}

#ctrl
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#entries th,
#ft li,
#ft li.current,
#ft li.fill,

div.pagelist,
footer li,
table.flashListing tr:nth-of-type(odd)
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

selector::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

selector:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

[data-cmd="x-disc"]
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%);
	border-radius: 22px!important;
	border: none!important;
