@-moz-document domain("4chan.org"), domain("boards.4channel.org") {
/* Image Settings */

#blotter td,
#entries tr,
#fp-menu li,

.top-box h2,
.top-box,

[name="post"],
table.flashListing tr:nth-of-type(even),
table.flashListing tr:nth-of-type(odd)
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#blotter td:hover,
#entries tr:hover,
#fp-menu li:hover,

.top-box h2:hover,
.top-box:hover,

[name="post"]:hover,
table.flashListing tr:nth-of-type(even):hover,
table.flashListing tr:nth-of-type(odd):hover
 {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

#fp-menu li:first-child:hover {
	border-top-left-radius: 22px;
}

#fp-menu li:last-child:hover {
	border-bottom-left-radius: 22px;
}


/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

