style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gangcity.nl") {


/* Instelling voor jezelf, kleurcodes kunnen veranderd worden.
*/

a[href="?x=daylight"]{
color: #006699 !important;
}

a[href="?bo=yes"]{
color: #006699 !important;
}

/* Instelling voor een door jou gekozen spelen, kunnen meerdere zijn.
*/

a[href="?breakout=Cinque"] , a[href="?breakout=killertje"] , a[href="?breakout=capone"], a[href="?breakout=daylight"]{
color: #CC0033 !important;
}


/* Instelling voor de input balkjes, kleurcodes kunnen veranderd worden.
*/

input[type="text"] {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:6px !important;
-moz-border-radius-bottomright:6px !important;
-moz-border-radius-topleft:6px !important;
-moz-border-radius-topright:6px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}

/* Instelling voor de textgrote en lettertype.
*/

* {
font-family:"verdana" !important;
font-size:9pt !important;
}

/* Voegt zoek speler toe
*/

form[action="zoeken.php"]{
background:transparent url(http://i34.tinypic.com/t7lnyq.png) no-repeat !important;
padding-top: 25px !important;
}

/* Input submit vakjes.
*/

input[type="submit"] {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:6px !important;
-moz-border-radius-bottomright:6px !important;
-moz-border-radius-topleft:6px !important;
-moz-border-radius-topright:6px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}

/* buttons.
*/

input[type="button"] {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:6px !important;
-moz-border-radius-bottomright:6px !important;
-moz-border-radius-topleft:6px !important;
-moz-border-radius-topright:6px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}


/* Input submit vakjes.
*/

input[type="reset"] {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:6px !important;
-moz-border-radius-bottomright:6px !important;
-moz-border-radius-topleft:6px !important;
-moz-border-radius-topright:6px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}


/* Select vakjes.
*/

select {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:3px !important;
-moz-border-radius-bottomright:3px !important;
-moz-border-radius-topleft:3px !important;
-moz-border-radius-topright:3px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}


/* Textarea.
*/

textarea {
color: #666666 !important;
-moz-appearance:none !important;
-moz-border-radius-bottomleft:6px !important;
-moz-border-radius-bottomright:6px !important;
-moz-border-radius-topleft:6px !important;
-moz-border-radius-topright:6px !important;
background:#333333 none repeat scroll 0 0 !important;
border:1px solid #666666 !important;
}



}



/* Gedeelte enkel voor begin.php
*/
@-moz-document url-prefix(http://www.gangcity.nl/begin.php) {

/* Verwijderd de je hebt nog geen msn invite melding
*/

td[bgcolor="#990000"]{
display: none !important;
}

/* Verwijderd de Internet Explorer word aangeraden melding
*/

#bar {
display: none !important;
}

}


@-moz-document url-prefix(http://www.gangcity.nl/forum.php) {

a:link {
color: #ffffff !important;
font-family: "verdana" !important;
text-decoration: underline !important;
}

a:visited {
color: #cccccc !important;
font-family: "verdana" !important;
text-decoration: none !important;


}

}
