style.css:
@-moz-document url-prefix("https://app.proficonf.com") {
.chat-message__text > .emoji {
	transform: scale(/*[[e-size]]*/);
	border-radius: /*[[e-radius]]*/px;
	margin: /*[[e-margin]]*/px;
}

/* Лайк */
.emoji_finger_up{
	background: center / 100% 100% no-repeat url("https://emoji.gg/assets/emoji/7294-like.gif");
}

