style.css:
@-moz-document domain("classic.shoutcast.com") {
  body > table:nth-of-type(1),
  body > table:nth-of-type(2),
  body > table:nth-of-type(3) > tbody > tr:nth-of-type(1),
  body > table:nth-of-type(3) > tbody > tr:nth-of-type(2) > td:nth-of-type(2),
  body > table:nth-of-type(4),
  body > table:nth-of-type(5),
  body > center
    { display: none !important }
}
