style.css:
@-moz-document domain("cigarboxnation.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.xg_theme #xg_navigation li a
{
	text-transform: capitalize!important;
}

.xg_theme .xg_module,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.xg_widget_main .module_video,
.xg_widget_main .detailed-leaderboard-module {
    overflow: hidden!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

body
{
	font-size: 1.85rem!important;
}

.dy-small,
.xg_theme .xg_user_generated,

small
{
	font-size: 1.52rem!important;
}

.dy-small,
.xg_theme .xg_user_generated,

small
{
	line-height: 2.1rem!important;
}


.xg_theme #xg_themebody
{
	line-height: 1.25rem!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

#comments dl.comment dd,
#xg_profiles_chatterwall_list dl.comment dd,

dl.comment dt
{
    margin-left: 4rem!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.xg_span-7,
.xg_span-14
{
	margin: 1.2rem!important;
}

.xg_theme .xg_blog .postbody .xg_user_generated img {
    margin: 12px 35%!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

.xg_widget_main .module_video,
.xg_widget_main .detailed-leaderboard-module
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.xg_span-14 img
{
	max-width: none!important;
}

#xg_navigation,

.xg_3col .xg_3col .module_groups .clist li,
.xg_span-14 img,
.xg_span-14 div.module_video .body_large ul.clist li
{
	width: auto!important;
}

.xg_span-14 #xg_canvas,
.xg_widget_main_index .xg_span-14 .xg_3col, .xg_widget_profiles_profile_show .xg_span-14 .xg_3col, .xg_widget_profiles_profile_apps.xg_span-14 .xg_3col {
    width: 100%!important;
}

.xg_theme #xg_head, .xg_theme #xg_body, .xg_theme #xg_foot
{
	width: 98%!important;
}

.xg_span-14 {
    width: 73%!important;
}

.xg_span-7 {
    width: 22%!important;
}
}

@-moz-document domain("cigarboxnation.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.xg_theme #xg_themebody,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.dy-small.xg_lightfont.indent,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

[style="font-weight: bold;"],
h3,
h4,
strong
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.xg_theme .xg_lightfont,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}


}

@-moz-document domain("cigarboxnation.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.xg_theme #xg_head
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#xg_overlay, .xg_overlay,

.xg_widget_main_index_index .module_chat_v4 .xg_chat.chatModule .xg_chatWindow,
.xg_theme #xg_body,
.xg_theme #xg_navigation ul li a,
.xg_theme .navigation,
.xg_theme .xg_headline,
.xg_theme .xg_module,
.xg_theme .xg_module_body,
.xg_theme .xg_module_foot,
.xg_theme .xg_module_head,

table tr.alt
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Orange 
_____________________________________________________________________________________________________*/

.pagination li a,
.xg_theme a.button,
.xg_theme button,
.xg_theme input.button
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.pagination li a::before,
.xg_theme a.button::before,
.xg_theme button::before,
.xg_theme input.button::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.pagination li a:hover::before,
.xg_theme a.button:hover::before,
.xg_theme button:hover::before,
.xg_theme input.button:hover::before
{
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.xg_span-7,
.xg_span-14
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.xg_chat.chatModule #userListContainer,
.xg_span-14 div.module_video .body_large ul.clist li,
.xg_theme .xg_lightborder,
.xg_theme .xj_sidebar_content .xg_module
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.xg_chat.chatModule #userListContainer:hover,
.xg_span-14 div.module_video .body_large ul.clist li:hover,
.xg_span-14:hover,
.xg_span-7:hover,
.xg_theme .xg_lightborder:hover,
.xg_theme .xj_sidebar_content .xg_module:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

#xn_bar,

.xg_theme #xg_navigation
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

#xg_navigation ul li.this,

.xg_theme #xg_navigation ul li a:hover
{
	background: linear-gradient(to top, #590505 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

table tr:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

}

@-moz-document domain("cigarboxnation.com") {
/* Image Settings */

#xg_themebody >#xg .xj_sidebar_content img,

.xg_chat.chatModule #userListContainer,
.module_forum .category,
.module_forum .vcard,
.xg_module.module_feed .xg_module_body,
.xg_span-14 div.module_video .body_large ul.clist li,
.xg_theme .xg_lightborder,

li,
p,
table tr
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

#xg_themebody >#xg .xj_sidebar_content img:hover,

.xg_chat.chatModule #userListContainer:hover,
.module_forum .category:hover,
.module_forum .vcard:hover,
.xg_module.module_feed .xg_module_body:hover,
.xg_span-14 div.module_video .body_large ul.clist li:hover,
.xg_theme .xg_lightborder:hover,

li:hover,
p:hover,
table tr:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("cigarboxnation.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Search ___________________________________________________________________________*/

#xn_bar #xn_bar_menu_search_submit
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

#xn_bar #xn_bar_menu_search_submit
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}



}

@-moz-document domain("cigarboxnation.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
