@-moz-document url-prefix("https://ilias.hs-heilbronn.de/") {
    ::-webkit-scrollbar {
        width: 20px;
        height: 20px;
    }

    ::-webkit-scrollbar-thumb {
        background: #3E3E58;
        border: 4px solid #292932;
        border-radius: 30px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #717199;
    }

    ::-webkit-scrollbar-track {
        border-radius: 0px;
        box-shadow: inset 0px 0px 0px 0px;
    }

    .ilCalSelList::-webkit-scrollbar-thumb {
        border: 4px solid #41414A;
        background: #35353d;
    }

    .ilCalSelList::-webkit-scrollbar-thumb:hover {
        background: #777789;
    }

    ::selection {
        background: #8860b3;
    }
}
