/* ==UserStyle==
@name           ToonHQ Dark Theme
@namespace      toonhq
@author         Nora
@description    `A dark theme for ToonHQ.`
@version        1.2.0
@license        MIT
==/UserStyle== */
@-moz-document domain("toonhq.org") {
    .theme.theme--generic-solid,
    nav.navbar.navbar-default,
    .theme.theme--home,
    .navbar-expand-lg,
    .theme__accent-bg,
    .theme.theme--invasions .theme__accent-bg,
    .theme.theme--groups .theme__accent-bg,
    .theme.theme--fishing .theme__accent-bg,
    .theme.theme--guilds .theme__accent-bg,
    .theme.theme--generic-solid .theme__accent-bg,
    .theme.theme--generic .theme__accent-bg,
    .navbar,
    .subnav--blue,
    .subnav--green,
    .sl .theme .theme--invasions,
    .theme.theme--fos .theme__accent-bg,
    .sl[data-v-68439b8a] {
        background: #202020;
        background-image: url(https://toonhq.org/static/dist/img/general_pattern.901019be.svg);
        background-repeat: repeat;
        background-size: 789.36px 761.26px;
        margin-bottom: 7px;
    }
    .sl__content[data-v-1d6821ca] {
        background: #483c5c;
        background-image: url(https://toonhq.org/static/dist/img/general_pattern.901019be.svg);
        background-repeat: repeat;
        background-size: 789.36px 761.26px;
    }
    .subnav.subnav {
        background: #ffffff00;
        color: #ffffff00;
    }
    .navbar-nav {
        margin-bottom: 7px;
    }
    .navbar.navbar-default .navbar-nav li.blue > a.active,
    .navbar.navbar-default .navbar-nav li.green > a.active,
    .navbar.navbar-default .navbar-nav li.steel > a.active,
    .navbar.navbar-default .navbar-nav li.fishing > a.active,
    .navbar.navbar-default .navbar-nav li.steel > a.active {
        background: #ffffff00!important;
    }
    .subnav__items li a,
    .subnav.subnav--fishing .subnav__items > li a,
    .guild__district[data-v-203cc74c] {
        color: #fff;
        font-size: 24px;
    }
    .info-card--fos .info-card__image {
        height: 92px;
        margin: 8px;
    }
    .subnav.subnav,
    .info-card--fos .info-card__title {
        color: #ffffff;
        padding-top: 5px;
    }
    .welcome[data-v-c0888612],
    .tt-card,
    .info-card--invasions,
    .info-card--groups,
    .card.card--alert,
    .info-card--fos {
        background: #202020;
        border-color: #380338;
        color: #FFFFFF;
        border: solid;
        border-radius: 32px;
    }
    .info-card--invasions,
    .info-card--groups,
    .info-card--fos {
        height: 115px;
    }
    .dropdown-menu,
    .dropdown-item,
    .header-toon__name h5 {
        background: #202020;
        color: #FFFFFF;
        border-radius: 32px;
    }
    .info-card--groups .invading-cog {
        background: transparent;
        border: 1px solid transparent;
        width: 100px;
        height: 100px;
        border-radius: 50%;
        opacity: 0.5;
        margin-left: 250px;
    }
    .info-card--invasions,
    .info-card--groups {
        padding: 5px;
        border: 3px solid #fff;
    }
    .vuedal.modal-content,
    .vue-dropdown__content {
        background: #202020;
        border-radius: 32px;
        border: #ffffff;
    }
    .group-message-count,
    .growing-textarea speedchat__chat-entry p-2,
    .dropdown-menu,
    .dropdown-item:hover,
    .header-toon__name h5,
    .subnav.subnav--fishing .subnav__items > li:hover a,
    .subnav.subnav--steel .subnav__items > li:hover a {
        color: #ffffff;
    }
    .gdetails {
        background: #202020;
        color: #b7aeae;
        border-radius: 24px;
    }
    .invasion-info-container {
        border-radius: 24px;
    }
    .features-curve[data-v-c0888612] {
        background: #ffffff00;
    }
    .table {
        color: #000;
        font-weight: 600;
        padding: 0.5rem;
    }
    .message__bubble[data-v-65ad3cfc] {
        color: #000;
        background: #ffffff;
        font-weight: 600;
    }
    .message__name,
    .message__time,
    h4.text-center.mb-0[data-v-b783264e],
    .h6,
    h6,
    .invasion-wanted[data-v-9534b75e],
    .invasion-info[data-v-9534b75e],
    .modal-body,
    .species[data-v-79af1111] {
        color: #fff;
    }
    .card.card--alert {
        background: #202020;
        margin: 5px;
        border-radius: 4px;
        font-size: 24px;
        padding: 2px;
    }
    .speedchat__header[data-v-9604629a],
    .user-dropdown .btn.btn-link.btn-lg {
        background: #20202000;
    }
    .speedchat__header-title[data-v-9604629a],
    .subnav.subnav--steel {
        color: #fff;
    }
    .speedchat__messages[data-v-9604629a] {
        color: #202020;
        background: #202020;
        padding: 10px 10px;
        border-radius: 24px;
    }
    .info-card__image {
        border-radius: 32px;
        margin-bottom: 10px;
        height: 100px;
    }
    .speedchat__chat-disabled-reason[data-v-9604629a] {
        color: #fff;
    }
    .table-info,
    .table-info > td,
    .table-info > th {
        background: #65156525
    }
    .btn.btn-darkblue,
    .subnav subnav--blue,
    .subnav.subnav--green .subnav__btn,
    .btn.btn-invasions,
    .btn.btn-groups,
    .btn.btn-guilds,
    .btn.subnav__btn.ml-3,
    .vue-swatches__fallback__button,
    .btn.btn-fishing,
    .btn.btn-generic-solid,
    .btn.btn-green,
    .btn-block,
    .btn-groups,
    .btn-option,
    .btn.btn-darkblue:hover,
    .dropdown--text button.dropdown-toggle,
    .subnav.subnav .subnav__btn {
        color: #fff !important;
        background: #651565;
        border: 2px solid #fff;
        border-radius: 24px;
    }
    .toonatar__center,
    .toon__track {
        color: #fff;
        border: 2px solid #fff;
        border-radius: 24px;
    }
    .speedchat__chat-entry[data-v-9604629a] {
        color: #fff;
    }
    .btn.btn-green.btn-option.active,
    .multiselect__option--selected.multiselect__option--highlight,
    .btn.btn-darkblue:hover,
    .btn.btn-groups:hover,
    .btn.subnav__btn.ml-3:hover,
    .subnav__btn:hover,
    .btn.btn-invasions:hover,
    .multiselect__option--highlight,
    .btn {
        background: #ef4fef;
    }
    .speedchat__footer[data-v-9604629a] {
        color: #fff;
        background: #202020;
        margin-right: 5px;
        margin-bottom: 5px;
    }
    .info-card__content {
        color: #fff;
        background: #202020;
        font-size: 14px
    }
    .modal-footer {
        background: #202020;
        border: 2px solid #202020;
        border-bottom-left-radius: 24px;
        border-bottom-right-radius: 24px;
    }
    .subnav.subnav--blue .subnav__items > li a,
    .subnav.subnav--fishing .subnav__items > li a {
        color: #651565;
    }
    .report-link {
        color: darkred;
    }
    .report-link:hover {
        color: red;
    }
    .subnav.subnav--blue .subnav__items > li a.active,
    .subnav.subnav--steel .subnav__items > li a.active,
    .subnav.subnav--fishing .subnav__items > li a.active {
        color: lime;
    }
    .info-card--invasions .info-card__title,
    .info-card--groups .info-card__title,
    .guild__district[data-v-71babf33],
    .subnav.subnav--blue .subnav__items > li:hover a,
    .no-groups[data-v-1b04abfc],
    .ft .text-muted[data-v-17027cf3] {
        color: #ffffff;
    }
    .ft .text-muted[data-v-17027cf3] {
        opacity: 1;
    }
    .info-card--groups .info-card__title {
        font-size: 8px;
    }
    .card[data-v-a5d191de],
    .table thead th,
    .table td,
    .table th {
        background: #202020;
        border-radius: 24px;
        color: #fff;
    }
    .modal-image-close[data-v-b783264e] {
        position: absolute;
        top: 1px;
        left: 1px;
        width: 0px;
        height: 0px;
        color: #00000000;
        background: #00000000;
    }
    .btn-group > .btn-group:not(:last-child) > .btn,
    .btn.btn-green.btn-option,
    .btn-group > .btn:not(:last-child):not(.dropdown-toggle) {
        border-radius: 8px;
        margin: 4px;
    }
    .multiselect__tags {
        color: #380338;
        border: 4px solid #651565;
    }
    .gdetails .invasion-info__body {
        color: #000;
    }
    .gdetails {
        color: #000;
        border-radius: 24px;
        border-color: white;
        border-width: 4px;
    }
    .gdetails .invasion-info {
        border: 5px solid #202020;
    }
    .subnav.subnav--steel .subnav__items > li a {
        color: #651565;
    }
    .btn.subnav__btn:hover,
    .btn.btn-generic-solid:hover,
    .btn.btn-fishing:hover,
    .btn.btn-guilds:hover,
    .btn.btn-green:hover,
    .btn.btn-block.btn-groups.disabled {
        background: red;
        border: 4px solid white;
    }
    .alert-primary {
        background: #202020;
        color: #fff;
        border: 2px solid white;
    }
    .multiselect__input,
    .multiselect__single,
    .__BVID__1806 {
        background: #202020;
        color: #fff;
    }
    .form-control,
    .multiselect__tags,
    .vue-swatches__wrapper,
    .vue-swatches__container,
    .vue-swatches__container:not(.vue-swatches--inline) {
        background-color: #202020;
        border-color: #fff;
    }
    .dropdown-item:hover .header-toon__name h5,
    .dropdown-item:hover {
        background-color: #fff;
        color: #202020;
    }
}
