style.css:
@-moz-document domain("userstyles.org") {
/* Stylish Dark Theme v5.0*/
/* Created by frokfrdk (formerly Lexan)*/

/* Dark Scrollbar*/
::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: var(--bg) !important;
    width: 10px;
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: var(--item) !important;
    border-radius: 10px;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: var(--item-hover) !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: var(--item-active) !important;
  }

/*Variables*/
:root {
    --bg: #1f2223;
    --bg2: #282B2C;
	--bg3: #333739;
    --item: #4E5456;
    --item-hover: #666;
    --item-active: #777;
    --white: #fff;
    --dark-white: #ddd;
    --darker-white: #aaa;
    --border-color: #1d1d1d;
    --text: #BFD5E8;
    --text-dark: #AABCCA;
}

/*Main Site*/
body {
    background-color: var(--bg) !important;
}

#nav-view-responsive /*Main Background*/{
    background-color: var(--bg) !important;
}

.us-stylecard--short{
    background-color: var(--bg3)
}

.us-stylecard--short:hover{
    background-color: var(--item)
}

h4 a,h4, h4 a:visited{
    color: var(--text) !important;
}

h4 a:hover{
    color: var(--white) !important;
    transition: .1s !important;
}

a:hover{
    color: var(--white) !important;
    transition: .1s !important;
}

a {
    color: var(--text) !important;
}

.us-stylecard--short .us-username{
    color: var(--text-dark)
}

.us-stylecard--short .us-stencil{
    box-shadow: none;
}

.see_more{
    background-color: var(--bg3)
}

.overlay_background{
    background-color: #000000aa !important;
}

/*Sidebar*/
.navigation{
    background-color: var(--bg2)
}

.navigation .navigation-category{
    color: var(--text)
}

.navigation .links-box .navigation-link .navigation-title, .pageNumber{
    color: var(--text)
}

.logo-navigation-link{
    color: var(--text) !important;
}

.navigation,.links-box,.navigation-link{
    box-shadow: none !important;
}

.navigation-link:hover{
    background-color: var(--bg3) !important;
    transition: .09s;
}
    
    .logo-navigation-link{
        filter: brightness(2.5);
    }
    
    #centralMsg{
        color: var(--text);
    }

/*footer*/
#footer{
    background-color: var(--bg3)
}

#copyrights{
    color: var(--text)
}

/*Theme install*/
.style_title,#headline,#infomation_value_left,#information_key{
    color: var(--text)
}

#share_div,#information_key_left,#information_value,#applies_link,.text_style_page{
    color: var(--text-dark)
}

#notes_textarea,#stylish-code{
    background-color: var(--bg2);
    color: var(--text-dark);
    border: none;
    font-family: Roboto !important;
}

.white_button,.css_button,#send_feedback_button,.Button{
    background-color: var(--bg3);
    color: #3498db;
    border: none;
    transition: all 0.2s ease;
}

.white_button:hover,.css_button:hover,#send_feedback_button:hover,.Button:hover{
    background-color: #d6deeb;
    color: #3498db;
    border: none;
    transition: all 0.2s ease;
}

button{
    color: var(--text);
    transition: .1s;
}
    
    .full-logo.black{
        filter: invert(1);
    }

#single_discussion{
    box-shadow: none;
}

.advancedsettings_shown{
    background-color: var(--bg2)
}

.title_setting,.label_image,.upload_image_label{
    color: var(--text)
}

select,.text_setting_input,.option-user-url {
    background-color: var(--bg);
    color: var(--text);
}
    
    .archived_text{
        color: var(--text)
    }
    
    .comment-content.edit /*comments*/{
    background: var(--bg2)
    }
    
    .comment-content{
        background: var(--bg2)
    }
    
    .comment-content .top .left-details .date{
        color: var(--text-dark);
        opacity: 1;
    }
    
    .comment-content .content-wrapper .content{
        color: var(--text)
    }
    
    .customize_button{
        background-color: #ddd
    }
    
    .eye_image{
        filter: invert(1)
    }
    
    #uninstall_style_button{
        color: var(--text)
    }
    
    #uninstall_style_button img{
        filter: brightness(3.4);
    }
    
    .action-button-wrapper .ctl-button img{
        filter: brightness(5);
    }
    
    .uninstall_stylish_style_button{
        filter: brightness(5);
    }
    
    .us-stylebox{
        background: var(--bg);
    }
    
/*Theme Install - Android*/
    .android-sp{
        background-color: var(--bg);
    }
    
    .android-sp-info{
        background-color: var(--bg2);
    }
    
    .android-sp-info .us-title{
        color: var(--text)
    }
    
/*Login*/
    .login-contact-link{
        color: var(--text)
    }

/*Your Profile*/
h2,th,dt,::marker{
    color: var(--text)
}

.date-value, .image-value,td{
    color: var(--text-dark)
}

.notice{
    background-color: #7e7e32
}

/*Profile*/
.us-stylecard--long{
    background-color: var(--bg3);
}

#pages a:visited, #pages a, #pages a:active, h1, .android_editor, .header, .title {
    color: var(--text) !important;
}

/*Form*/
#Head{
    background: #2E3E49;
}

.DiscussionAboutListDiscussion{
    color: var(--text)
}

p,.MItem:first-child,#Status,.Name{
    color: var(--text-dark)
}

.MItem{
    color: var(--darker-white)
}

/*Editing Themes*/
li,.linking-note,div.form-controls{
    color: var(--text-dark)
}

.form-controls label{
    color: var(--text)
}
    
    .Message,.Note.Closed.SignInOrRegister{
        color: var(--text-dark);
    }

input,textarea{
    background-color: var(--bg2);
    border: none;
    color: var(--text-dark)
}

/*Create Style*/
.choose_editor{
    background-color: #00000090
}

.choose_editor .boxesContainer .box{
    background-color: var(--bg3)
}
    
    .errorExplanation{
        background-color: #a44
    }
    
/*Creating Android Style*/
    .styleSettings .dropdowns .selectContainer .select,.styleSettings .dropdowns .selectContainer .dropdown_view,.styleSettings .inputs .inputZone input{
        background-color: var(--item);
        border: none;
        color: var(--text)
    }
    
    .imageHandler .nameAndActions .imageName{
        color: var(--text)
    }
    
    .license .options .option .text,.opacityController .control .showValue{
        color: var(--text-dark)
    }
    
    .designBox,.selectContainer .dropdown_view{
        background-color: var(--item)
    }
    
/*Tips page*/
    #help-style-settings-type,#help-style-settings-edit,#help-style-settings-placeholders,#help-style-settings-updating,h3{
        color: var(--text)
    }

pre{
    background-color: var(--bg2);
    border: 1px dashed var(--text);
    color: var(--text);
}

/*Contact page*/
.contact-bold-font{
    color: var(--text)
}

.contact-input,.contact-input-large{
    background-color: var(--bg3);
    color: var(--text);
    border-color: var(--text)
    }
    
    .contact-font,.answer{
        color: var(--text-dark)
    }
    
    .contact-submit-button{
        background-color: #7e7e32;
        color: var(--white)
    }
}
