style.css:
@-moz-document url-prefix(https://odysee.com/) {
html { 
		
	  /* Nomics Theme Colours/Tones */
				  
	--color-main-accent:#c70242!important; 
	--color-accent-two:#221e28!important;
	--color-accent-three:#352f42!important;
	--color-accent-four:#312643!important;
	--color-accent-five:#2a2537!important;
	--color-accent-not-active:rgba(62, 50, 82, .30)!important;
	--color-accent-active-light:#352947!important;    
    
	--color-accent-active:rgba(62, 50, 82, .61)!important; 
	--color-main-text:rgba(180, 180, 180, 0.8)!important;
	--color-text-mid:rgba(190, 190, 190, 0.8)!important;
	--color-text-light:rgba(240, 240, 240, 0.93)!important;

	--color-main-mid:rgba(200, 200, 200, 0.9)!important;		
	--color-main-bg:rgba(19, 13, 20, 0.91)!important;
	--color-sub-bg:rgba(19, 13, 20, 0.29)!important;
	--color-hover-bg:rgba(19, 13, 20, 0.4)!important;
	--color-post-bg:rgba(53, 45, 64, 0.25)!important;     
	--color-shade-trans:rgba(23, 21, 24, 0.75);  
	
		
	  /* Odysee Stlyes */
		
	--color-primary:var(--color-main-accent)!important;
	--color-fire: var(--color-primary)!important;
	--color-header-background: var(--color-main-bg)!important;
	--color-header-button: var(--color-accent-two)!important;
	--color-text: var(--color-main-text)!important;
	--color-button-secondary-text: var(--color-main-mid)!important;
	--color-input: var(--color-main-mid)!important;
	--color-background: var(--color-accent-two)!important;
	--color-background-base: var(--color-accent-two)!important;
    --color-button-toggle-bg-hover:var(--color-accent-active-light)!important;
    --color-accent-active:var(--color-accent-active-light)!important;
	--color-button-toggle-bg: var(--color-accent-active-light)!important;    
    
	}
	[theme=light] {
	
	}
	
	 body {
	 animation: fadein 2s; 
	}
	@keyframes fadein {
	 0% {opacity:0;}
	 50%{opacity:0.5;}
	 100% {opacity:1;}
	}
	
	*:focus, *:focus-visible, .header__navigation .wunderbar__wrapper:hover .wunderbar__input{
		box-shadow: 0px 0px 0px 1px #55304c inset!important;
		outline:none!important;
	}
	a.button.button--no-style.navigation-link.navigation-link--active span.button__label {
		color:#fff!important;
	}
	.header {
		background-color: var(--color-main-bg);
	}
	.header__navigation .wunderbar__wrapper .wunderbar__input {
		height: 35px!important;
	}
	.notification_list .notification__wrapper {
		background-color: var(--color-sub-bg)!important;
		margin-bottom: 12px!important;
		padding: 10px 0!important;    
	}
	.notification_list .notification__wrapper:hover {
		background-color: var(--color-hover-bg)!important;
	}
	.notification__wrapper--unread {
		box-shadow: none!important;
	}
	.navigation-links--micro .button__label {
		color: #6f707d!important;
	}
	.media__thumb {
		box-shadow: 0 0 0 1px var(--color-accent-three) inset!important;
	}
	.button--fire path {
		stroke: var(--color-fire)!important;
		fill: var(--color-fire)!important;
	}
	.notification__extra {
		flex-direction: row-reverse!important;
	}
	.navigation-links--absolute .navigation-link .button__label,  .button__label  {
		color: var(--color-main-text)!important;
	}
.notification__extra {
	width:120px!important;
	justify-content: flex-start!important;
}
.menu__link--notification {
	align-items: center!important;
}
.notification__icon {
	margin-top:0!important;
}
.claim-preview__repost-ribbon {
    box-shadow: 3px 3px 5px 0px rgb(0 0 0 / 33%);
}
.claim-preview--tile .claim-preview__repost-author .claim-preview__repost-ribbon .button .button__content .channel-name {
    font-weight: 400;
	color:#FFF!important;
}
.claim__tags .button__label, .button__label:not(h1.button__label) {
    color: var(--color-text-light)!important;
}
.header__navigationItem--balance .button__label  {
    color: var(--color-text-mid)!important;
}
.table--rewards .button__label {
    color: var(--color-main-accent)!important;
}
.button--uri-indicator .channel-name {
	color: #574d67!important;;
}
.button--uri-indicator .channel-name:hover {
	color: #9d7fc9!important;;
}
	/* POSTS */
	
.markdown-preview hr {
		border:none!important;
}


.card__title-actions .button-toggle, .card__title-actions .button--alt {
		background-color: var(--color-accent-not-active) !important;
}
.card__title-actions .button-toggle:hover, .card__title-actions .button--alt:hover {
		background-color: var(--color-accent-active) !important;
}
.card__title-actions .button-toggle--active {
		background-color: var(--color-accent-active-light) !important;
}
input, select, .date-picker-input {
		background-color: var(--color-accent-four)!important;
}
textarea {
	
		background-color: var(--color-sub-bg)!important;
}
.card__main-actions {
	border-top:none!important;
}
.post {
	font-family: system-ui!important;
    font-weight: lighter!important;
    letter-spacing: 0.05em!important;
    line-height: 2.1em!important;
    color: #bdb2c5!important;
		background: var(--color-post-bg)!important;  
		width: 100%!important;
		margin: 15px 0 30px 0;
		padding: 30px 4%!important;
		border-radius: 8px;
		box-shadow: 0 0 30px -2px rgba(0, 0, 0, 0.23);
	}
.post p {
		margin-bottom:1.7em!important;
}
	.section.card-stack.file-page__md {
		margin-top: 30px!important;
	}
	.file-page__post-comments>*, .file-page__md>* {
		width: 900px!important;
		margin: 0 auto!important;
	}
	@media (max-width: 900px) {
	.file-page__post-comments>*, .file-page__md>* {
		width:90%!important;
	}
	}
	.post h1, .markdown-preview h1, .markdown-preview h1:not(:first-child) {
		font-size:1.9em!important;
		/*text-transform: uppercase;*/
		margin-top:0!important;
		line-height: 1.2em!important;

	}
	.post h1 a .button__label {
  		color: var(--color-main-accent)!important;                                          
}
	.post h2 {
		font-size:1.2em;
		/*text-transform: uppercase;*/
	}
	.post__info {
		margin-bottom: 10px;
		padding-bottom: 15px;
		border-bottom: 1px solid var(--color-accent-three)!important;
	}
	.post blockquote p {
		margin: 0 20px!important;
	}
	.post blockquote {
		border-left: 0.2rem solid #c70242!important;
	}
	.post li, .post blockquote p {
		font-size: 88%!important;
		line-height: 1.4em!important;
	}
	.post ol, .post ul, .post blockquote {
		margin:20px 0!important;
		background: var(--color-accent-five)!important;
		padding: 25px 25px 25px 16px!important;
	}
	.file-render--post-container {
		margin-top:20px!important;
	}
	.file-page .file-page__md .file-viewer--document {
		margin-top:0!important;
	}
	.post .media__actions {
		margin-bottom: 30px 0!important;
	}
	.file-render--document {
		overflow: initial;
	}
	.claim-preview-metadata {
		flex-direction: rowimportant;
		display: fleximportant;
		flex: 1!important;
		flex-wrap: wrapimportant;
	}
	.claim-preview__title {
		color: #8c839a!important;
    	font-size: 1.4em;
	}
	.date_time {
		color: #6d627a!important;
	}
	.comment__meta-information:hover .date_time {
		color:  var(--color-main-accent)!important;

	}
.post .channel-name p {
	margin-bottom: 0!important;
    padding-top: 3px!important;
}	
.markdown-preview code {
	color: #917db5!important;
    font-size: 0.73em;
    background-color: #5f5a8024!important;
    padding: 0px 12px!important;
    margin: 5px 0!important;
    line-height: 2em;
}
.markdown-preview code:hover {
    color: #ffbdac!important;
}
.header__navigation .wunderbar__wrapper .wunderbar__input:focus-visible {
    outline: none!important;
}
.post .markdown-preview a .button__label, .markdown-preview a {
    font-size:0.9em!important;
    font-weight:normal!important;
    color: #9785adc7!important;;
}
.post .markdown-preview a:hover .button__label {
   /* color:var(--color-main-accent)!important*/
    text-decoration: underline!important; 
}
 /* NAG */

.nag--error {
	background-color: #151516!important;
    color: #898690!important;
    font-weight: 400!important;
}
.nag__button, .nag__button--error {
    background-color: #282828!important;
	border:none!important;
    color: #898690!important;
}
.nag__message::before {
	display: inline-block!important;
	content:""!important;
	vertical-align: middle!important;
	width:16px!important;
	height:16px!important;
	border-radius:100%!important;
	background-color:var(--color-main-accent)!important;
    margin-right:6px!important;
    animation: flash linear 1s infinite!important;
}



	/* Scrollbars */
	
	/* width */
	 html::-webkit-scrollbar, .navigation::-webkit-scrollbar  {
		width: 10px!important;
	}
	/* Track */
	 html::-webkit-scrollbar-track,  .navigation::-webkit-scrollbar-track {
		background: #1e1e1e!important;
	}
	/* Handle */
	 html::-webkit-scrollbar-thumb {
		background: #333!important;
	}
	.navigation::-webkit-scrollbar-thumb {
		background:var(--color-shade-two)!important;  
	}
	/* Handle on hover */
	 html::-webkit-scrollbar-thumb:hover {
		background:  var(--color-main-accent)!important;
	}
	.navigation::-webkit-scrollbar-thumb:hover {
		background: #2c2c2c!important; 
	}

	/* Animations */

@keyframes flash {
	0% { opacity: 1; } 
	50% { opacity: .1; } 
	100% { opacity: 1; }
}
}
