@-moz-document domain("www.goatlings.com") {
body {
	background: url(https://i.imgur.com/h8e3u4l.png) center !important;
	color: #e2e2e2 !important;
	background-position: center;
	background-repeat: no-repeat;
	background-size: contain;
	background-size: 100%;
	background-color: #0f0f0fe8 !important;
	size: 12pt;
}
/*Text of Site*/
#user-info {
	color: #fff;
}
/*ETOS*/
#user-info {
	width: 700px !important;
}

.explore-area
{
	background: #e2e2e2ab
}
.base_area,
.item_area,
.random_event,
#battle_log {
	border: 2px solid #00FF00;
}
element[style] {
	background-color: #3d3d3d9c;
}
input[type='button'] {
	border: 3px solid #191919;
	background: #00FF00;
	color: #191919;
}
body a:link,
a:visited {
	color: #00FF00 !important;
}
span[style] {
	color: #fff !important;
}
#wrapper {
	background: #191919ba !important;
}
#content {
	background: #3d3d3d52 !important;
	border: #3DAE3D;
}
tr:nth-child(even) {
	background: #e2e2e280 !important;
}
tr:nth-child(odd) {
	background: #e2e2e280 !important;
}
.forum-post {
	border-color: #262626 !important;
}
.poll-answer-block {
	background: #131313
}
.news-post {
	border: 1px solid #e2e2e269;
}
.forum-poll,
.table,
.bank-area,
.event,
.flash_data,
.cs-nav,
.cs-area,
.mail-message,
.battle-grid,
.item-invent-view {
	border: 1px solid #00FF00;
}
#m a:link,
a:visited,
b {
	color: #bcbcbc !important;
}
#m,
#n {
	border-color: #191919 !important;
}
#m {
	background: #3dae3d29 !important;
	color: #fff !important;
}
#n {
	color: #3DAE3D !important;
	background: #3dae3d29;
}
#table {
	background: #efefef !important;
	border-color: #00FF00 !important;
}
h1 {
	background: #191919 !important;
	border-bottom: #fff !important;
}
h1,
h2,
h3 {
	color: #3DAE3D !important;
	background: #262626 !important;
	border-bottom: 3px solid #0f0;
}
hr {
	border-top: 1px solid #00FF00;
}
.page-title {
	border-bottom: 1px solid #0f0;
}
table,
tr,
td {
	color: #fff;
}
