style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("break.com") {
#homepage_rgt, #custom_tab, #footer { display: none !important;}
#body { background: none !important; }
table td[width$="550"] { width: 900px !important; }
table td[width$="330"], td[width$="20"] { display: none !important; }
.pagination_links, .misc_hdr2, .top_cmore { width: 900px !important; }
.index_entry { width: 900px !important; }
.index_entry > div > h1 { width: 725px !important; }
.index_entry > div > h1 { background: none !important; }
.entry_desc { width: 600px !important; }
.entry_stats > table { position: absolute !important; right: 50px !important; }
}
