style.css:
@-moz-document domain("drawception.com")
{
  .text-muted
  {
    color: var(--text-color-muted) !important;
  }

  :root
  {
    --bg-accent-color: #fce3ca !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #FEB789 !important;
    --bg-hover-color: #5f4a8b !important;
    --bg-disabled-color: #4f3e72 !important;
    --bg-highlight-color: #9e7ac1 !important;
    --bg-duck-option: #9e7ac1 !important;
    --bg-duck-option-hover: #9e7ac1 !important;
    --text-color: #2e1b50 !important;
    --text-hover-color: #392164 !important;
    --text-color-lighter: #392164 !important;
    --text-color-darker: #160c26 !important;
    --text-color-muted: #160c26 !important;
    --border-color: #FEB789 !important;
    --border-highlight-color: #eba97f !important;
    --link-color: #af5081 !important;
    --link-color-hover: #f27c8a !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #5f4a8b !important;
    --store-btn-bg-hover-color: #eba97f !important;
    --store-btn-text-color: #fce3ca !important;
    --modal-overlay-color: rgba(0,0,0,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #160c26 !important;
    --navbar-bg-color: #9e7ac1 !important;
    --navbar-border-color: #8e6dae !important;
    --navbar-btn-text-color: #2e1b50 !important;
    --navbar-btn-text-hover-color: #3b2366 !important;
    --navbar-btn-border-color: #5f4a8b !important;
    --navbar-btn-border-hover-color: #765ea7 !important;
    --navbar-btn-text-shadow: 0px 0px 3px #fff !important;
    --progress-bar-bg-color: #cecece !important;
  }

  .text-danger
  {
    color: var(--border-color) !important;
  }

  .btn-primary
  {
    background-color: var(--navbar-btn-text-hover-color) !important;
  }

  a:focus, a:hover
  {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
  }

  .label-warning
  {
    background-color: var(--border-color) !important;
  }

  .theme-night
  {
    --bg-accent-color: #793abd !important;
    --bg-accent-highlight-color: #57ced3 !important;
    --bg-color: #06063c !important;
    --bg-hover-color: #4f95ff !important;
    --bg-disabled-color: #447dd3 !important;
    --bg-highlight-color: #124ba1 !important;
    --bg-duck-option: #124ba1 !important;
    --bg-duck-option-hover: #124ba1 !important;
    --text-color: #fffef9 !important;
    --text-hover-color: #c1c1c1 !important;
    --text-color-lighter: #68f9ff !important;
    --text-color-darker: #4db4b9 !important;
    --text-color-muted: #4db4b9 !important;
    --border-color: #68f9ff !important;
    --border-highlight-color: #793abd !important;
    --link-color: #ff44d3 !important;
    --link-color-hover: #ff96f8 !important;
    --comment-highlight-color: #ff96f8 !important;
    --store-btn-bg-color: #793abd !important;
    --store-btn-bg-hover-color: #ff44d3 !important;
    --store-btn-text-color: #fffef9 !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #793abd !important;
    --navbar-bg-color: #ff44d3 !important;
    --navbar-border-color: #793abd !important;
    --navbar-btn-text-color: #4e1340 !important;
    --navbar-btn-text-hover-color: #4f95ff !important;
    --navbar-btn-border-color: #793abd !important;
    --navbar-btn-border-hover-color: #4f95ff !important;
    --navbar-btn-text-shadow: 0px 0px 3px #ff44d3 !important;
    --progress-bar-bg-color: #e5f2ff !important;
  }

  .progressbar-bg
  {
    background-color: var(--navbar-btn-border-color) !important;
  }
}
