style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.steampowered.com")
{
  .page, html, body { background:#FFF !important; }

  a:hover, a:active, body_ahover { color: #000 !important; }
  .wysiwyg
  {
    background-color:#C0C0C0 !important;
    color:#FF3399 !important;
    background:#CC0066 !important;
  }

  .alt1, .alt1Active, .alt2, .alt2Active
  {
    background:#FFF !important;
    border-color:#000 !important;
    color:#383834 !important;
    font-weight:normal !important;
  }

  td.inlinemod { background:#C0C0C0 !important; }

  .panel, .panelsurround { background:#C0C0C0 !important; color: #383834 !important; }

  .thead, .tfoot
  {
    background:#C0C0C0 !important;
    border-color:#000 !important;
    color: #383834 !important;
  }

  .pagenav, .navbar strong { color: #383834 !important; }
  legend { background:#C0C0C0 !important; }

  .tcat
  {
    background:#C0C0C0 !important;
    border-color:#000 !important;
    color:#383834 !important;
    font-weight:normal !important;
  }

  .vbmenu_control a:link, .vbmenu_control_alink, .vbmenu_control a:visited,
  .vbmenu_control_avisited
  {
    background:#383834 !important;
    font-weight:normal !important;
    color:#d0d0d0 !important;
  }

  .vbmenu_popup .thead { background: #383834 !important; }

  #content .smallfont, #content .smallfont .time { color: #383834 !important; }
}
