    height: 12px!important;
    width: 12px!important;
    background: -webkit-linear-gradient(40deg , #000 , #1e1e1e , #000000 100%)!important;
    border-left: 0px solid #000 !important;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #888!important;
    border-radius: 0px!important;
  }


/*Outline Remover*/
  .annotator-container .html5-video-player
  {
    outline: 0 solid rgb(255, 255, 255) !important;
  }

/*Column*/
  .yt-alert-panel,.yt-alert-default.yt-alert-warn,#watch-action-panels,.upload-widget-right-column,.upload-widget-left-column,.creator-editor-nav,.creator-editor-content,.item-section,.primary-header-contents,#channel-subheader,#channels-browse-content-grid,.channel-header-profile-image,.yt-channel-msg-dialog .yt-dialog-footer,.yt-uix-overlay-actions,.vm-confirmation-overlay-header,.classic-uploader,.upload-item,.pl-video-handle,.pl-video,.branded-page-v2-primary-col-header-container,.about-metadata-container,.subscription-manager-filter-container,.subscription-manager-header,.collection-promo,.subscription-item,.yt-alert-actionable.yt-alert-info,#watch7-creator-bar,#watch-discussion,#watch7-action-panels,#watch7-action-buttons,#watch-header,#playlist-page-content,#creator-page-content,#creator-page-sidebar,.feed-item-container,.upload-thumb,.playlist-videos-list,.buttonbar
  {
    background-image: linear-gradient(to right bottom, rgba(66, 66, 66, 0.2) 52%, rgba(66, 66, 66, 0.3) 53%)!important;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #232323 !important;
  }

/*Column 2*/
  #mymedia,.waveform-slider,.waveform-player,.timedtext-content,.commerce-creator-signup-contract-view,.channel-watermark-uploader-content .watermark-example-container,.iph-dialog,.multitrack-timeline,.annotator-overlay-link-overview,#endscreen-editor-preview-guard,#dashboard-no-videos-overlay,.yt-alert-panel,#annotator-div,#hats-container.hats-categorical-style-horizontal,.featured-programming-editor .overview,.account-feature-v2-grid-items li,.feedback-banner,.yt-alert-default.yt-alert-warn,.yt-video-picker-grid .video-picker-item:hover,#yt-comments-sb-standin .box,.monetize-with-ads.monetize-options-box,#yts-article:after,.yt-alert-actionable.yt-alert-info,.yt-ui-menu-content,.yt-alert-default.yt-alert-success,.advanced-search-footer,.watch-playlist,.editor-thumb-body,li.track,#creator-page-content,.comment-entry,.yt-alert-default.yt-alert-info,.subscribers-table-item:nth-child(2n),.vm-list-view .vm-video-list li,.branded-page-box
  {
    background-image: linear-gradient(to right bottom, rgba(66, 66, 66, 0.2) 52%, rgba(66, 66, 66, 0.3) 53%)!important;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #232323 !important;
  }

/*Column 3 no box shadow*/
  .timeline-row,.comments .mod-list,.dashboard-widget-content
  {
    background-image: linear-gradient(to right bottom, rgba(66, 66, 66, 0.2) 52%, rgba(66, 66, 66, 0.3) 53%)!important;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #232323 !important;
  }

  .iph-dialog.create-playlist-widget-form .create-playlist-bottom-section,.hats-header,.live-dashboard .player-controls-buttons,.live-dashboard-status-bar,.sorterbar th,#yt-masthead-container
  {
    background-image: linear-gradient(to right bottom, rgba(66, 66, 66, 0.2) 52%, rgba(66, 66, 66, 0.3) 53%)!important;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #232323 !important;
  }

/*Drop Down*/
  #action-panel-details,.yt-uix-button-menu,.list,#watch7-sidebar,#appbar-guide-menu
  {
    background-image: linear-gradient(to right bottom, rgba(66, 66, 66, 0.2) 52%, rgba(66, 66, 66, 0.3) 53%)!important;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #232323 !important;
  }

/*Logo Expand Open*/
  #yt-masthead #logo
  {
    -webkit-animation-name: expandOpen;
    -webkit-animation-duration: 1.2s;
    -webkit-animation-timing-function: ease-out;
    visibility: visible !important;
  }



/*Column ease-in-out*/
#gh-banner,#channels-browse-content-grid,#player,#action-panel-details,#watch-header,#watch7-sidebar-contents,.vm-video-list li,.dashboard-widget-content,.creator-editor-nav,.creator-editor-content,.branded-page-box
{
  -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 1.5s;
  -webkit-animation-timing-function: ease-in-out;
  visibility: visible !important;
}

@-webkit-keyframes fadeIn
{
  0%
  {
    -webkit-transform: scale(0);
    opacity: 0.0;
  }

  60%
  {
    -webkit-transform: scale(1.1);
  }

  80%
  {
    -webkit-transform: scale(0.9);
    opacity: 1;
  }

  100%
  {
    -webkit-transform: scale(1);
    opacity: 1;
  }
}


                      
/*Comment Column Tweaks*/                     
.comments .comment-entry
{
  padding: 5px !important;
  margin-bottom: 10px !important;
}

