style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://www.youtube.com/watch")
{
.ytp-ce-element.ytp-ce-element-show,
.iv-branding .branding-img-container img,
.ytp-big-mode .ytp-chrome-controls .ytp-fullerscreen-edu-button.ytp-button {
    visibility: hidden !important;
}

#voice-search-button {
    visibility: hidden !important;
}

#container.ytd-masthead,
#masthead-positioner,
#yt-masthead-container {
    height: 35px !important;
    margin: 0 !important;
    padding: 0 !important;
    z-index: 9999 !important;
}

ytd-masthead,
#masthead-positioner,
#yt-masthead-container {
    background-color: #00000080 !important;
}

ytd-searchbox.ytd-masthead {
    justify-content: left !important;
    margin: 0 !important;
    opacity: 0.5 !important;
}

yt-formatted-string#super-title.ytd-watch-metadata {
    font-size: 1.0rem;
    line-height: 1.3rem;
    display: block;
}

ytd-watch-flexy:not([fullscreen]) .title.ytd-video-primary-info-renderer,
#eow-title,
body:not(.no-scroll) div#title.style-scope.ytd-watch-metadata {
    position: fixed !important;
    top: 3px !important;
    right: 227px !important;
    font-size: 14px !important;
    color: #ffffff80 !important;
    z-index: 99999 !important;
}

div#title yt-formatted-string#original-info.style-scope.ytd-watch-metadata {
    display:none;
}

div#title h1 yt-formatted-string.style-scope.ytd-watch-metadata {
    font-size: 14px !important;
}

ytd-watch-metadata:not([smaller-yt-sans-light-title]) h1.ytd-watch-metadata
{
    line-height: 1.4rem;
}

ytd-app:not([masthead-hidden]) #page-manager.ytd-app {

    --ytd-masthead-height: 35px !important;
}

/* Player Size */
ytd-watch-flexy[theater]:not([fullscreen]) {
    --theater-top-offset: var(--ytd-masthead-height, 56px);
}

ytd-watch-flexy[theater]:not([fullscreen]) #player-wide-container.ytd-watch-flexy {
    height: calc(100vh - var(--theater-top-offset));
    min-height: 0;
    max-height: none;
}

#center.ytd-masthead {
    left: 177px !important;
    position: absolute !important;
    flex: unset !important;
    width: 400px !important;
}

.watch-stage-mode #theater-background,
#placeholder-player .player-api,
#player .player-api {
    top: -14px !important;
    height: calc(0.5625 * 100vw) !important;
    max-height: calc(100vh - 35px) !important;
    min-height: 480px !important;
}

#button.ytd-button-renderer,
#search-icon-legacy.ytd-searchbox {
    border: initial !important;
}

ytd-searchbox[desktop-sbox-icon="icon_20_ptext_primary"] #container.ytd-searchbox,
#search-container.ytd-searchbox-spt,
#search-form.ytd-searchbox
{
    margin: 0px;
    max-width: 200px;
}

#search-form.ytd-searchbox {
    height: 37px;
}

.ytp-chrome-bottom {
    opacity: 0.4;
}

.ytp-chrome-top .ytp-share-button-visible,
.ytp-chrome-top {
    opacity: 0.4 !important;
}

.ytp-big-mode .ytp-title-text {
    font-size: 14px !important;
    text-align: right !important;
}

.ytp-big-mode .ytp-playlist-menu-button-icon,
.ytp-big-mode .ytp-share-icon,
.ytp-big-mode .ytp-cards-button,
.ytp-chrome-top .ytp-cards-button {
    opacity: 0.4 !important;
}
}
