style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mixi.jp") {
iframe[width="468"]{visibility:hidden;}
td[width="224"][valign="middle"],
a[href="premium.pl"]{display:none !important;}
table[width="565"]{width:795px !important;}
table[width="460"]{width:690px !important;}
}

@-moz-document url-prefix("http://mixi.jp/view_community.pl?id="),url-prefix("http://mixi.jp/view_bbs.pl?id=") {
iframe[width="710"],
script+table[width="720"]
{display:none !important;}
}

@-moz-document url("http://mixi.jp/show_log.pl") {
td[width="140"],td[width="120"]{visibility:hidden;}
}
