style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ScrapBook Dark Sidebar */
@-moz-document url(chrome://scrapbook/content/scrapbook.xul){
treechildren::-moz-tree-row {
border: none !important; background: transparent !important;}
treechildren {background: #3D3D3D !important;}
treechildren::-moz-tree-cell-text {color: #DDD !important;}
treechildren::-moz-tree-separator {border-color: #555 !important;}
treechildren::-moz-tree-row(selected) {background-color: #5D5D5D !important;}
treechildren::-moz-tree-row(selected, focus) {background-color: #777 !important;
}

/* ScrapBook Tree Item icon */
treechildren::-moz-tree-image(sbTreeColTitle, leaf),
richlistitem image.sblist-icon {
list-style-image: url(data:image/png;base64,) !important;
}
}
