style.css:
/*This code is based on yaasir ahmad-sanni (naadir500) https://userstyles.org/styles/162809*/
@-moz-document domain("youtube.com") {
/* General Parts */
  .ytp-chrome-bottom
  {
    bottom: -7px;
    padding-left: 25px;
    margin: -14px -37px;
  }

  .ytp-chrome-top,
.ytp-chrome-bottom
  {
    text-shadow: 0 0 0px rgba(0, 0, 0, .5);
  }

  .ytp-chrome-controls
  {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==);
    height: 25px;
  }

  .ytp-right-controls
  {
    height: 100%;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==);
    margin: 0 -32px;
 /*PATCH: Set from 24px to 32px*/;
  }

  .ytp-time-display
  {
    font-size: 10.4px;
    font-family: Roboto, Helvicta, Arial, sans-serif;
    padding: 0 0px;
    width: 57px;
    margin-left: 8px;
    margin-top: -8px;
 /*PATCH: Add an -8px top margin*/
    line-height: 27px;
  }

  .ytp-time-current
  {
    color: #fff!important;
  }

  .ytp-time-separator,
.ytp-time-duration
  {
    color: #999;
  }

  .ytp-gradient-bottom
  {
    height: 0px;
    padding-top: 0px;
  }

  .ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after
  {
    top: 2px;
    right: 2px;
    background-color: #b50324;
  }

  .ytp-settings-button.ytp-hd-quality-badge::after
  {
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png);
    background-position: 26px 0px;
  }

  .ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge::after
  {
    content: '___';
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png);
    background-position: 26px 0px;
    margin: -12px -16px;
    color: transparent;
    height: 7px;
  }

  .ytp-chrome-controls .ytp-button
  {
    height: 200%;
 /*PATCH: Set from 100 to 250*/
    border-right: 1px solid #222;
  }

  .ytp-bezel
  {
    border-radius: 10px;
  }

/* Scrubber */
  .ytp-scrubber-button.ytp-swatch-background-color
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px -19px;
  }

  .ytp-scrubber-button.ytp-swatch-background-color:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px 0;
  }

  .ytp-scrubber-button
  {
    width: 13px;
    padding: 1.5px;
    margin: 0px 0px 0px 0;
    border-radius: 36.5px;
  }

/* Progress-Bar */
  .ytp-progress-bar-container:hover:not([aria-disabled=true])
  {
    cursor: pointer;
    height: 8px !important;
  }

  .ytp-swatch-background-color
  {
    background-image: linear-gradient(to bottom, #c00 0, #600 100%);
  }

  .ytp-progress-list
  {
    background: #1e1f1f;
    background-image: linear-gradient(to top, rgba(30, 31, 31, .75) 89%, #2f2f2f 90%);
  }

  .ytp-load-progress
  {
    background-image: linear-gradient(to top, #5a5a5a 89%, #666 90%);
  }

  .ytp-hover-progress-light
  {
    opacity: 0 !important;
  }

  .ytp-big-mode .ytp-scrubber-button
  {
    height: 16px !important;
  }

  .ytp-progress-list
  {
    transform: scaleY(0.6) scaleX(1.037) translateX(12.0px) !important;
  }

  .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list
  {
    transform: scaleX(1.037) translateX(12.0px) !important;
  }


/* Play */
  .ytp-chrome-controls .ytp-play-button[title="Play (k)"]
  {
    width: 15px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -231px;
    padding: 0px 27.5px;
    right: 50px;
  }

/*There seems to be an unworkable issue with this to my knowledge,
  as the aria-label doesn't change when the pause/play function is
  invoked and while the title does, it then can't be used with the
  ":hover" call.
  
  Unfixable by me, but someone more knowledgable may be able to 
  deal with it.*/
  button.ytp-play-button.ytp-button:hover[aria-label="Pause (k)"]:hover
  {
    width: 15px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -42px;
    padding: 0px 27.5px;
    right: 50px;
  }


/* Pause */
  button.ytp-play-button.ytp-button[title="Pause (k)"]
  {
    width: 15px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -177px;
    padding: 0px 27.5px;
    right: 50px;
  }

  button.ytp-play-button.ytp-button:hover
  {
    width: 15px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -258px;
    padding: 0px 27.5px;
    right: 50px;
  }


/* Vol */
  button.ytp-mute-button.ytp-button[title="Mute (m)"]
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -160px -258px;
    width: 30px;
    margin-left: 8px;
    margin-right: 5px;
  }

  button.ytp-mute-button.ytp-button:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -123px;
    width: 30px;
    margin-left: 8px;
    margin-right: 5px;
    padding: 0 33px 0 2px;
    margin-top: 0px;
  }


/* Mute */
  button.ytp-mute-button.ytp-button[title="Unmute (m)"]
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -15px;
    width: 30px;
    margin-left: 8px;
    margin-right: 5px;
  }

  button.ytp-mute-button.ytp-button[title="Unmute (m)"]:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) 0 -42px;
    width: 30px;
    margin-left: 8px;
    margin-right: 5px;
  }


/* Next + Previous */
  a.ytp-next-button.ytp-button
  {
    display: none !important;
  }

  a.ytp-prev-button.ytp-button
  {
    display: none !important;
  }



/* Fullscreen */
  button.ytp-fullscreen-button.ytp-button[title="Full screen (f)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -86px -123px;
    padding: 0px 23px;
  }

  button.ytp-fullscreen-button.ytp-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -93px -96px;
    padding: 0px 23px;
  }


/* Exit Fullscreen */
  button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) 6px -68px;
    padding: 0px 23px;
  }

/*Unusable due to the same issue associated with the pause hover*/
  button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"]:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -149px;
    padding: 0px 15px;
  }


/* Theater Mode */
  button.ytp-size-button.ytp-button[title="Theater mode (t)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -89px -15px;
    background-size: auto;
    padding: 0 14.5px;
  }

  button.ytp-size-button.ytp-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -204px;
    background-size: auto;
    padding: 0 14.5px;
  }


/* Default Mode */
  button.ytp-size-button.ytp-button[title="Default view (t)"]
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -57px -123px;
    background-size: auto;
  }

  button.ytp-size-button.ytp-button[title="Default view (t)"]:focus:hover
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -231px;
    background-size: auto;
  }


/* Settings */
  button.ytp-button.ytp-settings-button[title="Settings"]
  {
    width: 30px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -155px -123px;
    padding: 0 14.5px;
    margin-left: 3px;
    background-size: auto;
  }

  button.ytp-button.ytp-settings-button:hover
  {
    width: 30px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -130px -204px;
    padding: 0 14.5px;
    margin-left: 3px;
    background-size: auto;
  }

  button.ytp-button.ytp-settings-button:active
  {
    width: 30px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -130px -204px;
    padding: 0 14.5px;
    margin-left: 3px;
    background-size: auto;
  }


/* Captions */
  button.ytp-subtitles-button.ytp-button[title="Subtitles/closed captions (c)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -204px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-subtitles-button.ytp-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -285px;
    padding: 0 14.5px;
    background-size: auto;
  }

  .ytp-chrome-controls .ytp-button[aria-pressed=true]:after
  {
    width: 0px;
    left: 9px;
    -moz-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
    -webkit-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
    transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
  }

  button.ytp-subtitles-button.ytp-button[aria-pressed="true"]
  {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-subtitles-button.ytp-button[aria-pressed="true"]:hover
  {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

/* MiniPlayer (for users of the material design) */
  button.ytp-miniplayer-button.ytp-button
  {
    width: 34px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -39px -123px;
    margin-right: 0px;
    margin-left: -20px;
    background-size: auto;
  }

  button.ytp-miniplayer-button.ytp-button:hover
  {
    width: 34px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -28px -231px;
    margin-right: 0px;
    margin-left: -6px;
    background-size: auto;
  }


/* Volume Slider */
  .ytp-volume-slider-handle:before
  {
    left: -64px;
    background: url(data:image/png;base64,);
    background-position: 16px !important;
    height: 120px;
  }

  .ytp-volume-slider-handle:after
  {
    left: 5px;
    background: url(data:image/png;base64,) !important;
    background-position: 151px !important;
    height: 120px;
  }

  .ytp-volume-slider-handle
  {
    position: absolute;
    top: 60%;
    width: 5px;
    height: 14px;
    border-radius: 0px;
    margin-top: -19.9px;
    background: url(data:image/png;base64,) 258px 0px !important;
  }

  .ytp-volume-slider-handle[style="left: 0px;"]
  {
    left: -9px !important;
  }

  .ytp-volume-slider-active .ytp-volume-panel
  {
    width: 54px;
    margin-right: 3px;
  }

  .ytp-volume-slider-handle
  {
    margin-left: 9px;
  }


/* Settings Box */
  .ytp-settings-menu
  {
    right: 12px;
    background: rgba(31, 31, 31, .9) !important;
    bottom: 36px;
    z-index: 71;
    will-change: width, height;
  }

  .ytp-menuitem[aria-haspopup=true] .ytp-menuitem-content
  {
    padding-right: 10px;
    background: #e6e6e6;
    color: #555;
  }

  .ytp-menuitem
  {
    height: 23px;
  }

  .ytp-menuitem[aria-haspopup="true"]:not([aria-disabled=true]):hover
  {
    background-color: rgba(255, 255, 255, 0);
  }

  .ytp-menuitem[role="menuitemcheckbox"]:not([aria-disabled=true]):hover
  {
    background-color: rgba(255, 255, 255, 0);
  }

  .ytp-menuitem-toggle-checkbox
  {
    border-radius: 0px;
  }

  .ytp-menuitem-toggle-checkbox:after
  {
    height: 13.91px;
    width: 16.91px;
    border-radius: 0px;
    margin-top: 0.4px;
    background-color: #bdbdbd;
    box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0.6);
    transition: all 0s cubic-bezier(0.4, 0.0, 1, 1);
  }

  .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:after
  {
    background-color: #636263;
    margin: 0px 3px;
    -moz-transform: translateX(16px);
    -ms-transform: translateX(16px);
    -webkit-transform: translateX(16px);
    transform: translateX(16px);
  }

  .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox
  {
    background: #bdbdbd;
  }

  .ytp-popup
  {
    text-shadow: 0 0 2px rgba(255, 255, 255, 0);
  }

  .ytp-menu-label-secondary
  {
    font-size: 91%;
    color: #555;
  }

  .ytp-panel[style="width: 296px; height: 183px;"]
  {
    position: absolute;
    bottom: -5px;
    right: 0px;
    width: 257px !important;
    height: 110px !important;
    overflow-y: auto;
    overflow-x: hidden;
  }


/* Right-Click Menu */
  .ytp-panel-menu[style="height: 174px;"]
  {
    color: #333;
    margin-left: -32px;
  }

  .ytp-popup
  {
    border-radius: 0px;
    background: #ffffff;
  }

  .ytp-menuitem-label
  {
    font-size: 118%;
    font-weight: 100;
  }

  .ytp-menuitem:not([aria-disabled=true]):hover
  {
    background-color: #333;
    color: #fff;
  }

  .ytp-popup[class="ytp-popup ytp-contextmenu"]
  {
    border-radius: 2px;
    width: 223px !important;
    height: 177px !important;
    background: #ffffff;
    border: 1px solid #ccc;
  }

  .ytp-menuitem[data-visual-id="9"]
  {
    height: 25px;
  }

  .ytp-panel[style="width: 295px; height: 174px;"]
  {
    bottom: -4px;
    right: 0px;
    width: 295px !important;
    height: 180px !important;
  }

  .ytp-menuitem-label,
.ytp-menuitem-content
  {
    display: table-cell;
    vertical-align: middle;
    padding: 2px 26px;
  }

  .ytp-panel-menu[style="height: 177px;"]
  {
    color: #333;
  }

  .ytp-panel-menu[style="height: 178px;"]
  {
    color: #333;
  }

  .ytp-menuitem-label,
.ytp-menuitem-content
  {
    display: table-cell;
    vertical-align: middle;
    padding: 2px 26px;
  }



/* Fullscreen Mode */
  .ytp-big-mode .ytp-chrome-top 
.ytp-big-mode .ytp-chrome-controls
  {
    width: 0px !important;
  }

  .ytp-big-mode .ytp-time-display
  {
    line-height: 48px;
  }

  .ytp-big-mode .ytp-chrome-controls
  {
    height: 32px;
    line-height: 27px;
  }

  .ytp-big-mode .ytp-chrome-bottom
  {
    height: 54px;
    padding-top: 4px;
    margin-bottom: -21px !important;
  }

  .ytp-big-mode .ytp-progress-list
  {
    transform: scaleY(0.625) scaleX(1.020) translateX(16px) !important;
  }

  .ytp-big-mode .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list
  {
    transform: translateY(-2.5px) scaleX(1.020) translateX(16px) !important;
  }

/* Fullscreen Mode Volume Slider */
  .ytp-big-mode .ytp-volume-slider-handle:before,
.ytp-big-mode .ytp-volume-slider-handle:after
  {
    height: 14px;
    margin-top: -5px;
    width: 85px;
  }

  .ytp-big-mode .ytp-volume-slider-handle
  {
    width: 0px;
    height: 0px;
    border-radius: 9px;
    margin-top: -15px;
  }

  .ytp-big-mode .ytp-volume-slider-handle:before
  {
    left: -64px;
  }

  .ytp-big-mode .ytp-volume-slider-active .ytp-volume-panel
  {
    width: 55px;
    margin-right: 0px;
  }

  .ytp-big-mode .ytp-volume-slider-handle[style="left: 0px;"]
  {
    left: 11px !important;
  }

  .ytp-big-mode .ytp-volume-slider-handle
  {
    margin-left: -11px;
  }


/* Play on TV Button */
  button.ytp-button[title="Play on TV"]
  {
    display: none !important;
  }

/* Watch on YouTube Button */
  .ytp-chrome-controls .ytp-button.ytp-youtube-button
  {
    width: 0px;
    padding-right: 52px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -302px -34px;
  }

  .ytp-chrome-controls .ytp-button.ytp-youtube-button:hover
  {
    width: 0px;
    padding-right: 52px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -15px 1px;
  }

/* Spinner */
  .ytp-spinner-rotator
  {
    width: 31px;
    height: 31px;
    background: url(data:image/png;base64,);
  }

  .ytp-spinner-container
  {
    width: 31px;
    padding-bottom: 31px;
    margin-top: -15px;
    margin-left: -15px;
    transform: rotate(617688888888grad);
  }

  .ytp-spinner-left
  {
    right: 49%;
    opacity: 0;
  }

  .ytp-spinner-right
  {
    left: 49%;
    opacity: 0;
  }

/* Replay */
  .ytp-chrome-controls .ytp-play-button[title="Replay"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -285px;
    padding: 0px 27.5px;
  }

  .ytp-chrome-controls .ytp-play-button[title="Replay"]:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -15px;
    padding: 0px 27.5px;
  }
}
