style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("murientor.grammex.com") {

body, body div, body div div, body div div div {background-color:#eee !important;
                color:#000 !important;}

.postmsg, .postleft, .postmsg span, .postsignature, .postfootleft {color:#000 !important; background-color:#fff !important}

table, table th, td div, td.tcl div {background-color:#fff !important;}

div.dokuwiki {background-color:#fff !important;
              color:#000 !important; width: 800px !important}

div {background-color:#fff !important; color:#000 !important;}

.button {color:#009 !important; text-decoration:underline !important; float:right !important; background-color:#eee !important;}

div.dokuwiki div#sidebar a:hover, .topnav a:hover, li a:hover {background-color:#ccc !important;} 

a:link, a:active, a:hover, a:visited, a.wikilink:hover {color:blue !important;}

h1 a, h1 a:hover, h2 a, h2 a:hover, h3 a, h3 a:hover, .titlebar a, .titlebar a:hover  {color:#000 !important;}

div.box, div#punwrap {width:800px !important; margin:auto !important;}

img {display: none !important;}

.level1, .level2 {color:#000 !important;}

.page, .page div {background-color: #fff !important;}

.comment_wrapper {width: 90% !important; padding-left:0px !important; margin-left:0px !important;
}

h2 {background-color: #eee !important;} 
td {background-color: #fff !important;}

dl dt, dl dd {background-color: #fff !important}
fieldset legend {color:#000 !important; font-weight:bold !important}

div.postright, div.postfootright, div.postfootleft, div.postleft, div.clearer {background-color:#fff !important; border-left-color:#fff !important}

div.inbox {background-color:#fff !important}
div.linkst div, div.postlinksb div {background-color:#eee !important; }
}
