style.css:
@-moz-document domain("zenmoney.ru") {
html,body {background-color: #fbfaf9; /*font-family: "PT Sans" !important;*/}
.transaction {background-color: #fbfaf9}
.transaction.hover, #infoscrollData .trans:hover {background-color: #fff !important}
.money, .amount {font-family: "Verdana"}
.money .cent {color: inherit; opacity: .3}
.transaction .balance .symbol,
.accounts_total .symbol,
.money .symbol{opacity: .3; margin-top: 1px; font-size: 14px}
.transaction .money.plus {color: #008322;}
.money.minus,
.total_owe {color: red;}
.transaction .balance .transfer .money {color: #007fed !important;}
.transaction .account {font-style: italic; opacity: .5}
.transaction .date {color: inherit; font-weight: bold;}
.transaction .date .remained {color: inherit; opacity: .3; font-weight: normal; visibility: hidden}

.transaction {border-radius: 10px;}
.transaction div {height: auto !important;}

/* Меню, навигация */
li.active {
	border-radius: 3px;
}
li.active a:hover {color: #fff !important;}

/* Вкладки-переключатели */
.filterBlock .filter-type li.active,
.zenForm .typeMenu ul li.active,
#accountFilter li.active,
h1 .headerLink.active {border-radius: 5px; background-color: #feffb5;}

#accountFilter li:hover {background-color: #fff;}
#accountFilter li.active:hover {background-color: #f6f79b;}
#accountFilter li,
#accountFilter li.active {
	border: 0px solid red;
	margin-bottom: 0;
	padding: 5px;
	margin-left: -5px;
}
#accountFilter li .filter-item {
	padding: 0 20px 0 0 !important;
	background-color: transparent !important;
	border: 0 !important;
	background-position-y: -2px !important;
	left: 0 !important;
}
#accountFilter li .balance {margin-top: 0;}




.transaction.hover .date .remained {visibility: visible}
.transaction .date .remained,
.transaction .action,
.transaction .info .comment {display: block;margin-top: 7px;}

.transaction .action .delete {color:red;}
.transaction .action .delete:hover {color:#ff8400 !important;}

a:hover,
touch,
#accountFilter li:hover a {color: #36a8ef !important; text-decoration: none !important;}

/* Выбор категории */
.jf_treeobj .active:hover {
	color: #fff !important;
}
.jf-popup {
}

.cell-1-1 h1,
.cell-1-2 h1 {display: none;}


/* Приимечание транзакций */
.transaction .date .remained,
.transaction .action,
.transaction .info .comment {
	white-space: break-spaces;
}



/* Авторизация */
.zenLoginForm {
	padding: 10px 20px;
}
#loginOrRegisterSubmit {
	width: 205px;
}
.zenLoginForm table td.zlfLoginPwd {
	border-right: 2px solid #fff;
}
.zenLoginForm table td.zlfOpenId {
	padding-left:20px;
}
.zenLoginForm h2 .separate .thin {
	color: #d3d1cd;
}
.zenLoginForm h2 .signUp {
	margin-left: 3.1ex;
}


/* Форма добавления */
.transactionForm-form,
.transactionForm-add,
.transactionForm-many,
.transactionForm-adjust,
#rule-header,
.editForm,
.zenLoginForm {
	border: 0;
	background-color: #f2efe0;
	border-radius: 10px;
}
.zenForm {
	display: flex;
	width: 100%;
}
.zenForm .dateCol {
	width: auto;
	float: none;
	padding-right: 15px;
}
.zenForm .formCol {
	width: 100%;
	float: none;
	padding-right: 10px;
}
.jquery-zenTag,
.jquery-zenTag .tags-path {
	width: auto !important;
}
.jquery-zenTag .tags-input {
	display: block;
}



/* Теги, категории */
.jf-tag:first-child {
	margin-left: 0px;
}
.jf-tag {
	background-color: #fdffb4;
	margin: 0;
	margin-bottom: 3px;
	border-radius: 3px;
}
.jf-tag:nth-child(even) {
	background-color: #fdffb4;
}
.jquery-zenTag {
	margin-top: 10px;
}
.tags-inform {
	display: none;
}



/* Кнопки */
input[type="submit"],
input[type="button"],
button.submit {
	background: linear-gradient(#c2c0bd, #a7a5a2);
	color: #fff;
	border-radius: 5px;
	border: 0;
	padding: 7px 14px 8px 14px !important;
}
input[type="submit"]:hover,
input[type="button"]:hover,
button.submit:hover {
	background: linear-gradient(#d3d1cd, #b5b3b0);
}
input[type="submit"]:active,
input[type="button"]:active,
button.submit:active {
	background: linear-gradient(#9b9993, #7b7976);
	padding-top: 9px !important;
	padding-bottom: 6px !important;
	box-shadow: 0px 2px 4px -1px rgb(102 90 79) inset;
	color: #e9e8e0;
}
.finalInputTrans {
	display: flex;
	width: auto;
	flex-direction: row;
	align-content: stretch;
	justify-content: flex-start;
}
input[type="submit"] + .cancelWrap {
	padding-top: 7px;
	padding-left: 4px;
	display: block;
}

/* Инпуты */
.zenForm input[type="text"],
.zenForm .zenForm-account {
	border: 1px solid #d6d4c2 !important;
	height: 1.3em !important;
	border-radius: 3px;
}
/*.zenForm select {
	-webkit-appearance: menulist-button !important;
	line-height: 1.3em !important;
	border: 1px solid red !important;
}*/
.zenForm input[type="text"]:focus-visible,
.zenForm .zenForm-account:focus-visible {
	outline-width: 4px !important;
}
.zenForm .addComOrReci textarea,
.zenForm .zenForm-debtReminderEmail textarea {
	width: 100%;
}



/* Календарь */
td.datepickerNotInMonth a {color: #aaa;}
div.datepicker {background-color: transparent}
.datepickerToday {}
tbody.datepickerDays td.datepickerToday a {
	border: 1px solid #000 !important;
	color: #000;
	margin: -1px -1px 0px -1px;
	font-weight: bold;
}
tbody.datepickerDays td.datepickerSelected {background: none; background-color: #f04136 !important;}

/* Календарь: заголовок */
div.datepicker table {background: none;}
div.datepicker .datepickerGoNext a,
div.datepicker .datepickerGoPrev a,
div.datepicker .datepickerMonth a { color: #666 }

div.datepicker tbody.datepickerDays td {background-color: #fff; border: 0}
div.datepicker tbody.datepickerDays td:hover {background-color: #007fed !important; color: #fff !important; border: 0}
div.datepicker tbody.datepickerDays td:hover a {color: #fff !important;}
div.datepicker tbody.datepickerDays td.datepickerNotInMonth {background-color: transparent;}

/* Календарь: выходные */
div.datepicker tbody.datepickerDays td.datepickerSaturday a:link,
div.datepicker tbody.datepickerDays td.datepickerSunday a:link {color: red; opacity: .5}
.datepickerDoW th {padding: 0 0 10px 0 !important;}

/* Календарь в отчётах */
.zenForm .datepickerContainer {
	padding: 0;
}
.datepickerContainer {
	background-color: #f2efe0;
	border-radius: 10px;
	border-top-left-radius: 0;
	padding: 10px
}


/* Главная */
#mainWrapper > div,
#mainWrapper > .hr,
.ue-tab-container,
#mainWrapper .pageBlock2 .zenGrid-1 {display: none}
#mainWrapper .pageBlock2 {display: block}
#mainWrapper .pageBlock2 .zenGrid-2 {float: none; margin: 20% auto;}

/* Настройки */
.account-item.active, .edit .account-item.active {
	background-color: #f6f79b;
}
.tag-list ul.childs,
.tag-list ul.childs li {
	background-color: transparent !important;
}
.tag-list li:hover {
	background-color: #fff !important;
}
.tag-list li.under {
	background-color: #fff !important;
}

/* Отчёты */
.addForm .omnibox,
.dropSelect>a {
	background-color: transparent;
	border-color: transparent;
}
}
