style.css:
@-moz-document url-prefix("http://www.easychirp.com/"), url-prefix("http://www.easychirp.com"), url-prefix("https://www.easychirp.com") {
/* Nav tweet */
#navTweet{
 background-color:#333333;
 min-height:43px;
 color:#ffffff;
 transform:translatex(0px) translatey(3px);
 display:flex;
 justify-content:normal;
 align-items:center;
}

/* Link */
#navTweet .nav-item a{
 background-color:rgba(255,255,255,0) !important;
 border-style:none;
 color:#ffffff !important;
 font-weight:400;
}

 /* Wrapper */
 #wrapper{
  position:relative;
  top:-16px;
 }
  
/* Body */
body{
 font-family:Arial,'Helvetica Neue',Helvetica,sans-serif;
 display:flex;
}

body.theme-default {
     background-color:#dfdfdf !important;
}

#main .rounded{
 border-style:none;
}

/* Link */
#navTweet .nav-item li a{
 border-style:none;
 border-top-left-radius:0px;
 border-top-right-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 background-color:#fee091 !important;
 color:#070707 !important;
 font-size:14px;
 font-weight:500;
}

.theme-inverse #main .tweet .quotedTweet {
background-color: #484848;
}

.theme-inverse #enterTweet {
background-color: #727272;
}

.theme-inverse #main .content {
background-color: #363636;
}
.theme-inverse #main .tweet {
    border-color:#505050;
}

.theme-inverse #navMain li li a, .theme-inverse #navTweet li a {
background-color: #fee091;
}
/* Link */
#navTweet .nav-item:nth-child(7) > a:nth-child(1){
 transform:translatex(12px) translatey(4px);
 background-image:url("https://github.com/tnbr/thenarrative/raw/master/icons8-search-20.png");
 background-repeat:no-repeat;
 background-position-x:0%;
 background-position-y:35%;
 color:rgba(255,255,255,0) !important;
}

/* Link */
#navMain .nav-item:nth-child(3) a{
 display:none;
}

/* Link */
#navMain .nav-item:nth-child(4) a{
 display:none;
}

/* Link */
#navMain .nav-item:nth-child(5) a{
 display:none;
}

/* Link */
#navMain .nav-item:nth-child(6) a{
 display:none;
}

/* Open */
#navTweet .nav-item .open:nth-child(1){
 transform:translatex(0px) translatey(0px);
}

/* Link */
#navTweet .nav-item:nth-child(3) a{
 display:none;
}

/* Link */
#navTweet .nav-item:nth-child(5) a{
 display:none;
}


/* Enter tweet */
#enterTweet{
 border-style:none !important;
 background-color:#eee;
 border-top-left-radius:0px;
 border-top-right-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 min-height:36px;
}

/* Content Division */
#main .content{
 background-color:#ffffff;
}

/* Tweet */
#wrapper #main .content .tweet{
 border-bottom-style:solid !important;
}

/* Tweet */
#main .tweet{
 transform:translatex(0px) translatey(3px);
 border-color:rgba(122,122,122,0);
 border-bottom-color:#d3d3d3;
 border-top-right-radius:0px;
 border-top-left-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 border-bottom-width:1px;
}

/* Tweet menu wrapper */
#tweet_menu_wrapper{
 background-color:#50a7e6;
 color:#ffffff;
}

/* Link */
#navMain .nav-item:nth-child(2) > a:nth-child(1){
 color:#ffffff;
 font-size:13px;
}

/* Link */
#navMain .nav-item a{
 color:#ffffff;
 font-size:13px;
 text-transform:none;
 font-weight:600;
 letter-spacing:-0.1px;
 word-spacing:-0.4px;
 border-style:none;
}

/* Link */
#navMain .nav-item li a{
 color:#000000;
 border-top-right-radius:0px;
 border-top-left-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 background-color:#fee091;
}

/* Link */
#wrapper div #main_menu_wrapper #navMain .nav-item a{
 transform:translatex(0px) translatey(3px) !important;
}

/* Current */
#navTweet .nav-item:nth-child(1) a{
 font-size:16px;
 font-weight:700;
 background-image:url("https://github.com/tnbr/thenarrative/raw/master/icons8-home-24.png");
 background-repeat:no-repeat;
 color:rgba(255,255,255,0) !important;
 transform:translatex(12px) translatey(4px);
 width:20px;
 background-position-y:12%;
}


/* Logo container */
#logoContainer{
 display:none;
}

/* Link */
#skip a{
 display:none;
}

/* Heading */
#wrapper h1{
 display:none;
}

/* Paragraph */
#main .tweet p{
 font-size:13px;
}

/* Options tweet */
#main h3 .btnOptionsTweet{
 font-size:11px;
}

/* Options user */
#main h3 .btnOptionsUser{
 font-size:13px;
}

/* Shorten */
#btnShorten{
 border-style:solid;
 border-top-right-radius:0px;
 border-top-left-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 border-color:#c1c1c1;
 border-width:1px;
}

/* Button */
#frmUrlShort fieldset button{
 border-width:0px;
 border-top-left-radius:0px;
 border-top-right-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
}

/* Enter tweet */
#txtEnterTweet{
 font-family:Arial,'Helvetica Neue',Helvetica,sans-serif;
 font-size:17px !important;
}

/* Post */
#frmSubmitTweet div .btnPost{
 border-width:1px;
 border-top-left-radius:0px;
 border-top-right-radius:0px;
 border-bottom-left-radius:0px;
 border-bottom-right-radius:0px;
 font-size:18px;
 border-color:#bcbcbc;
 background-color:#3695d9;
 color:#ffffff;
}

/* Link */
#navMain .nav-item:nth-child(1) a{
 background-image:url("https://github.com/tnbr/thenarrative/raw/master/easychirp-logo.png");
 background-repeat:no-repeat;
 word-spacing:1px;
 background-position-x:6%;
}

/* Control enter tweet */
#controlEnterTweet{
 margin-top:2px;
}

/* Quoted tweet */
#main .tweet .quotedTweet{
 background-color:#ededed;
 border-style:none;
}

/* Link */
#main q a{
 font-weight:500;
}

/* Link */
#wrapper #main .content .tweet q a{
 color:#0882cd !important;
}

@media (max-width:440px){
 
 /* Wrapper */
 #wrapper{
  margin-left:0px;
  margin-right:0px;
 }
 
 /* Enter tweet */
 #enterTweet{
  padding-top:15px;
  transform:translatex(0px) translatey(-13px);
  margin-bottom:0px;
 }
 
 /* Link */
 #navMain .nav-item:nth-child(1) a{
  padding-left:30px;
  padding-top:0px;
 }
 
 /* Quotation */
 #main .tweet q{
  font-size:17px;
 }
  /* Paragraph */
 #welcome p{
  display:none;
 }
 
 /* Welcome */
 #welcome{
  display:none;
 }
}

@media (min-width:441px){

 /* Enter tweet */
 #enterTweet{
  margin-top:0px;
  padding-top:15px;
  padding-bottom:15px;
 }
 
 /* Link */
 #navMain .nav-item:nth-child(1) a{
  padding-right:8px;
  padding-left:31px;
 }
 
}

@media (min-width:801px){

 /* Quotation */
 #main .tweet q{
  font-size:17px;
 }
 
}
}
