style.css:
@-moz-document url-prefix("https://www.roblox.com/redeem"), url-prefix("https://www.roblox.com/redeem") {
/* WARNING!!! ONLY WITH JS CODE */
/* Old Font */

*
{
    font-family: "Source Sans Pro", Arial,Helvetica,sans-serif !important;
}

/* ~/CSS/Fonts/SourceSansPro-Google.css */
@font-face {
font-family: 'Source Sans Pro';
font-style: normal;
font-weight: 300;
src: local('Source Sans Pro Light'), local('SourceSansPro-Light'), 
url(https://fonts.gstatic.com/s/sourcesanspro/v9/toadOcfmlt9b38dHJxOBGMVNtom4QlEDNJaqqqzqdSs.woff) format('woff');
}
@font-face {
font-family: 'Source Sans Pro';
font-style: normal;
font-weight: 400;
src: local('Source Sans Pro'), local('SourceSansPro-Regular'), url(https://fonts.gstatic.com/s/sourcesanspro/v9/ODelI1aHBYDBqgeIAH2zlNHq-FFgoDNV3GTKpHwuvtI.woff) format('woff');
}
@font-face {
font-family: 'Source Sans Pro';
font-style: normal;
font-weight: 600;
src: local('Source Sans Pro Semibold'), local('SourceSansPro-Semibold'), url(https://fonts.gstatic.com/s/sourcesanspro/v9/toadOcfmlt9b38dHJxOBGCmgpmuQqK2I-L2S9cF65Ek.woff) format('woff');
}
.light-theme .content {
    background-color: #fff !important;
    color: #191919 !important;
    padding-top: 0px !important;
    margin-top: 0px;
}
div#container-main {
    background: #ffffff !important;
    height: 580px !important;
    padding-bottom: 25px !important;
}
html {
    background-color: #fff !important;
}
#wrap {
    background-color: #fff !important;
}
.light-theme .alert-info {
    background-color: #f68802;
    font-size: 20px !important;
}
.container-header {
    margin: 0;
}
#Body > h1 {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    display: inline-block;
    padding: 0;
    font-size: 32px;
    font-weight: bold;
    margin: 12px;
    color: #343434;
    letter-spacing: -1px;
    line-height: 1.3em;
}
#Body > h1 > span {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif !important;
    font-size: 32px;
    font-weight: bold;
    color: #343434;
    letter-spacing: -1px;
    line-height: 1.3em;
}
#RedeemContainer {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    color: #343434;
    font-size: 14px;
    line-height: 1.428;
    z-index: 1;
    clear: both;
}
#Instructions {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    color: #343434;
    font-size: 14px;
    line-height: 1.428;
    width: 438px;
    float: left;
    border-right: 1px solid #a9a9a9;
    padding-bottom: 5px;
    padding-right: 10px;
}
.header {
    font-weight: bold !important;
    font-size: 15px !important;
    margin-bottom: 4px !important;
}
#Instructions .listitem {
    margin-bottom: 15px;
    font-size: 14px;
}
.footnote {
    margin-top: 35px;
    color: #666;
    font-size: 13.8px !important;
}
.right-column {
    display: none !important;
}
.left-column {
    float: right !important;
    top: -204px;
    left: -41px;
}
#redeem-gift-card > div.col-sm-12.col-md-6.left-column > div > div.container-header {
    display: none;
}
.redeem-gift-card-form {
    margin-top: 6% !important;
    margin-left: 66px !important;
}
#headerPromo {
    font-weight: bold !important;
    font-size: 15px !important;
    margin-bottom: 4px !important;
    cursor: inherit;
}
.error-input-field {
    margin: 0px !important;
}
input#promotion-code-input {
    border: 1px solid #767676 !important;
    border-radius: 2px !important;
    height: 27px !important;
    width: 146px !important;
    font-size: 12px !important;
    padding: 1px 2px !important;
    color: #000 !important;
    margin-top: 20px;
}
input#promotion-code-input:hover {
    border: 1px solid #4f4f4f !important;
    border-radius: 2px !important;
    height: 27px !important;
    width: 146px !important;
    font-size: 12px !important;
    padding: 1px 2px !important;
    color: #000 !important;
}
input#promotion-code-input:active {
    border: 1px solid #8d8d8d !important;
    border-radius: 2px !important;
    height: 27px !important;
    width: 146px !important;
    font-size: 12px !important;
    padding: 1px 2px !important;
    color: #000 !important;
}
.legal-agreement.text-description.legal-agreement-content {
    display: none !important;
}
.light-theme .btn-primary-lg {
color: #fff;
    background-color: #428bca;
    border-color: #357ebd;
    background-image: url(https://static.rbxcdn.com/images/Buttons/StyleGuide/bg-btn-green.png)!important;
    padding: 1px 7px 0 7px;
    height: 23px;
    min-width: 40px;
    font-size: 14px;
    line-height: 18px;
    background-position: left -160px;
    border-radius: 0px !important;
    width: fit-content !important;
    position: absolute;
    top: 69px;
    left: 215px !important;
}
.light-theme .btn-primary-lg:hover {
    color: #fff;
    background-color: #3071a9 !important;
    border-color: #285e8e !important;
    background-position: left -181px!important;
}
.light-theme .btn-primary-lg:focus {
    background-color: #428bca;
    border-color: #357ebd;
}
#redeem-gift-card > div.col-sm-12.col-md-6.left-column > div > form > div.loading-space > span {
    background-image: url(https://images.rbxcdn.com/21e504e643e6c21e0c90e5a1b03325f9.gif) !important;
    width: 30px;
    height: 30px;
    position: relative;
    left: 220px;
    top: -40px;
}
}
