@-moz-document domain("gab.com") {
/* Colors */
 :root[theme=muted] {
	/* posts background color */
	--solid_color_primary: #242423!important;
	--solid_color_primary-opaque: rgba(128, 206, 225, 0.6)!important;
	/* 	button backgrounds */
	--solid_color_secondary: #212529!important;
	/* 	hover */
	--solid_color_secondary-dark: black!important;
	/* 	main background */
	--solid_color_tertiary: #343a40!important;
	/* 	comments bacground color */
	--solid_color_block: #343a40!important;
	/* 	post text */
	--text_color_primary: #e9ecef!important;
	/* @s, times, like, comment, etc */
	--text_color_secondary: #80cee1!important;
	/* 	copyrights, menus */
	--text_color_tertiary: #FFF!important;
	--text_color_alt: #cbf3f0!important;
	--border_color_secondary: var(--text_color_primary);
	--color_input_placeholder: var(--text_color_secondary);
	/* nav stuff */
	--navigation_search: var(--solid_color_tertiary)!important;
	--navigation_background: var(--solid_color_primary)!important;
	/* 	new posts in notifs */
	--solid_color_highlight: #0E131F!important;
	--border_color_secondary: #50514f!important;
	/* 	Text Highlighting */
	--color_highlight: #C2A11E;
	/* 	gab logo and stuff */
	--color_brand: #C2A22E!important;
	--color_brand-dark: var(--color_brand);
	--color_brand-light: var(--color_brand);
	--color_brand-light-opaque: var(--color_brand);
	--fs_xxl: 2.3rem;
	--color_black: black!important;
	--color_gold: #B0931C!important;
}
