.container, .shopify-policy__container.shopify-policy__container
{
	max-width: none!important;
}

.shopify-section--main-customers-register,
.shopify-section .container
{
	width: 98%!important;
}

.anyflexbox .content .wrap
{
	width: 88%!important;
}

}

