@keyframes slidein {
  from {
    transform: translateX(-100%);
    opacity:0;
  }

  to {
    transform: translateX(0%);
    opacity:1;
  }
}

@keyframes slideOver {
  from {
    transform: translateX(0%);
    opacity:0;
  }

  to {
    transform: translateX(100%);
    opacity:1;
  }
}
