style.css:
@-moz-document regexp("https://((meta\\.)?stackoverflow|([-\\w]+\\.)?stackexchange)\\.com/(search.*|questions((\\?|/tagged).*)?)?$") {
  /* SEARCH, QUESTION LIST */
  html.html__unpinned-leftnav body > .container,
  body>.container,
  #content {
    max-width: none;
  }
  #content {
    padding: 1rem 2rem;
    display: flex;
  }
  #sidebar, .sidebar {
    width: 17em;
  }
  #mainbar, .mainbar {
    width: 100%;
  }
  #content:before, #content:after {
    content: none;
  }
  #questions, #qlist-wrapper {
    margin: 0;
  }
  .s-post-summary--stats:not(#\0) {
    flex-flow: row nowrap;
    width: auto;
    font-size: smaller;
    line-height: 1;
    text-align: center;
    align-items: center;
  }
  .s-post-summary--stats-item-number {
    font-size: 200%;
  }
  .s-post-summary--stats-item, .s-post-summary--stats-item-number {
    display: block !important;
  }
  .s-post-summary--stats-item:not(.is-deleted) {
    padding: 2px 4px !important;
    border: none !important;
    width: 4em !important;
    margin: 0 !important;
  }
  .s-post-summary--stats-item.is-deleted:not(#\0) {
    position: absolute;
    bottom: 0;
    margin: 0;
    padding: 1px 3px;
    z-index: 1;
  }
  .s-post-summary--stats-item.is-deleted svg {
    display: none !important;
  }
  .s-post-summary--stats-item__emphasized {
    color: #0a0 !important;
  }
  .s-post-summary--stats-item__emphasized[title*=" -"] {
    color: #c00 !important;
  }
  .s-post-summary--stats-item:nth-child(3) * {
    font-weight: 100 !important;
  }
  .s-post-summary--stats-item[title^="0 "], .s-post-summary--stats-item[title$=" 0"] {
    color: #8888 !important;
  }
  .s-post-summary--stats .has-answers:not(.has-accepted-answer) {
    background: #15b11533;
  }
  .s-post-summary {
    padding: .5em 0;
  }
  .s-post-summary--content-title {
    flex: 0 0 50%;
    margin: 0 !important;
  }
  .s-post-summary--content-excerpt {
    width: 30%;
  }
  @media (max-width: 1440px) {
    .s-post-summary--content-excerpt {
      display: none !important;
    }
  }
  .s-post-summary--content {
    display: flex;
    align-items: center;
  }
  .s-user-card__minimal {
    width: 10em;
  }
  .s-user-card--avatar {
    order: 1;
  }
  .s-post-summary--meta {
    flex-wrap: nowrap;
    flex: 1;
  }
  .s-post-summary--meta-tags {
    flex: 1;
    display: block !important;
  }
}
@-moz-document regexp("https://((meta\\.)?stackoverflow|([-\\w]+\\.)?stackexchange)\\.com/search.*") {
  /* SEARCH */
  .stats {
    display: flex;
    line-height: 1;
    width: auto;
  }
  #content .stats > * {
    padding: 3px;
    width: 4.25em;
  }
  #content .answered {
    background: #15b11533;
  }
  #content .status {
    border: none;
  }
  #content .stats strong {
    font-size: 2rem;
  }
  .statscontainer {
    width: auto;
  }
  .summary {
    display: flex;
  }
  .result-link {
    flex: 0 0 25%;
  }
  .excerpt {
    flex: 0 0 45%;
  }
  .tags {
    display: block !important;
    flex: 1;
  }
  .started.float-right {
    text-align: right;
    flex: 0 0 10em;
  }
  .votes, .status {
    margin: 0;
  }
  .question-summary {
    padding: 8px;
  }
  .summary > * {
    padding: 0 !important;
  }
}
