*/ 
/* ============ COLOR PALETTE ============ */
:root {
    --grey: #717171;
    --grey-dark: #5a5a5a;
    --grey-light: #8d8d8d;
    --blue-day: #306AF2;
    --blue-night: #4789E5;
    --red-day: #F8333C;
    --red-night: #ee353e;
    --green-day: #28B75C;
    --green-night: #20944B;
    /* Day */
    --day-darker: #7d7d7d;
    --day-dark: #B7B7B7;
    --day-light: #DDDDDD;
    --day-lighter: #EAEAEA;
    --day-lightest: #FFFFFF;
    /* Night */
    --night-darkest: #1C1F28;
    --night-darker: #232733;
    --night-dark: #333949;
    --night-light: #6B7699;
    --night-lighter: #9CA2B7;
}
/* Cream White Theme */
html:not([style-scope]):not(.style-scope) {
    --yt-spec-brand-background-primary: var(--day-lightest) !important;
}
ytd-masthead, app-toolbar.ytd-c4-tabbed-header-renderer {
    box-shadow: var(--masterhead-shadow) !important;
}

/* Day */
html:not([style-scope]):not(.style-scope) {
