style.css:
@-moz-document url("https://addons.mozilla.org/en-US/statistics") {
#header { display: none; }
#content-main { position: absolute; width: auto; height: auto; margin: 0; max-width: 100%; top: 2em; }
#footer { display: none; }
h1 { display: none; }
.secondary { position: absolute; left: 0; margin: 0; padding: 0; width: 100%; max-width: 100%; float: none; font-size: 150% }
.highlight { position: absolute; left: 0; top: 2em; margin: 0; padding: 0; width: 100%; max-width: 100%; float: none; }
.featured { width: 100%; max-width: 100%; float: none; clear: both; }
.listing-footer { display: none; }
h3 { display: none; }
.prose { display: none; }
.featured-inner { margin-top: 2em; }
#stats-table-listing { display: none; }
}

@-moz-document domain("crash-stats.mozilla.com") {
#page-header { display: none; background: none; }
#signatureList { display: block; position: absolute; top: 0; left: 0; margin: 0 auto; width: 100%; height: 100%; background: #fff; }
#footer { display: none; }
h1 { display: none; }
}

@-moz-document url("http://feeds.mozilla.com/") {
#intro, #nav, .groups, #footer { display: none; }
}

@-moz-document url("https://ldap.mozilla.org/phonebook/#search/random") {
#header { display: none; }
#results { position: relative; top: 5em; margin: 0 auto; }
}
