style.css:
@-moz-document domain("www.moongift.jp") {
  body {
    font-size: 100% !important;
  }
  #container {
    min-width: 0 !important;
  }
  #left, #right {
    width: 100% !important;
    margin: 0 !important;
  }
  .subcontainer + .subcontainer {
    display: none !important;
  }
  .subcontainer:last-child {
    display: block !important;
  }
}
