style.css:
@-moz-document domain("mail.google.com"), domain("mail.google.com.br"), domain("hangouts.google.com") {
/***** HEADER *****/

/* color and drop shadow */
 .nH.oy8Mbf.qp {
    background: rgb(81, 134, 236) !important;
    box-shadow: 0 0 4px rgba(0, 0, 0, .14), 0 4px 8px rgba(0, 0, 0, .28);
}
/* icons */
 header svg {
    color: white !important;
    opacity: 1 !important;
}
/*** status box ***/

/* text */
 .Yc {
    color: white;
}
/* indicator */
 .Yg, .aaE, .dzLpDb {
    border: 1px solid rgba(255, 255, 255, .5) !important;
    border-radius: 50%;
    fill: none !important;
}
/*** account ***/

/* google color outline */
 .gb_Xa {
    display: none;
}
/* replacement outline */
 .gb_Aa.gbii {
    border: 1px solid white;
}
/* google workplace junk */
 .gb_ia {
    display: none;
}
.gb_ga.gb_jg.gb_f {
    width: auto !important;
    border-radius: 0;
    background: transparent;
    border: none;
}
/*** GMAIL logo ***/
 .gb_rc.gb_ie img {
    display: none;
}
/* replacement ENVELOPE LOGO */
 .gb_rc.gb_ie a {
    width: 50px;
    transition: filter .08s linear;
}
.gb_rc.gb_ie a:after {
    content:url("data:image/svg+xml;utf8,<svg viewBox='0 0 623 385' xmlns='http://www.w3.org/2000/svg' style='fill:rgb(250,250,250);'><path d='M310.876,240.967l56.382,-34.991l255.242,158.404l0,5.37c-0,8.284 -6.716,15 -15,15c-85.189,0 -507.311,0 -592.5,0c-8.284,-0 -15,-6.716 -15,-15c-0,-3.062 -0,-5.37 -0,-5.37l254.556,-158.476l56.32,35.063Zm-72.711,-47.446l-238.165,147.464l-0,-294.928l238.165,147.464Zm146.054,-0l238.281,147.464l0,-294.928l-238.281,147.464Zm238.281,-178.521c-0,-8.284 -6.716,-15 -15,-15l-592.5,-0c-8.284,0 -15,6.716 -15,15l-0,9.149l310.761,193.67l311.739,-193.67l-0,-9.149Z'/></svg>");
}
.gb_rc.gb_ie a:hover {
    filter: brightness(120%) drop-shadow(1px 3px 5px #555);
}
/*** search bar ***/
 header form {
    margin-left: 9px !important;
}
/* search bar icons */
 header form svg {
    color: grey !important;
    opacity: 1 !important;
}
/***** BODY *****/

/* space body down so that header shadow has room */
 .nH.bkL, .nH.bAw.nn, .Tm.aeJ {
    padding-top: 5px;
}
/*** left side menu ***/
 .TO .nU>.n0, .TO.NQ .nU>.n0, .TO.nZ .nU>.n0, .ah9>.CJ, .n3>.CL>.CK {
    font-size: 9pt;
    color: rgb(97, 97, 97);
}
.TN.bzz:hover {
    background: rgba(97, 97, 97, .05);
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
}
.aqn.aIH.nH.oy8Mbf.apV {
    transition: all .2s;
}
/* center area */
 .bkK > .nH {
    border-radius: 2px !important;
}
.Nu::-webkit-scrollbar {
    width: 8px;
}
.Nu::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, .3) !important;
    border-radius: 8px;
}
/* compose button */
 .z0>.L3, .bhZ:not(.bym) .z0>.L3 {
    font-size: 0;
    height: 48px;
    width: 48px;
    min-width: 48px;
    padding: 0;
    position: absolute !important;
    border-radius: 48px;
}
.z0>.L3::before {
    width: 48px;
    min-width: 48px;
}
/*** ICONS ***/

/* all */
 .TO .qj {
    opacity: 1 !important;
}
.TO.NQ .qj {
    opacity: 1 !important;
}
/*** colors ***/

/*
filters calculated using:
https://stackoverflow.com/questions/42966641/how-to-transform-black-into-any-given-color-using-only-css-filters/43960991#43960991
*/
 .NQ {
    background-color: white !important;
    box-shadow: inset 0 0 0 1px rgb(91, 134, 228, .5);
}
/* inbox */
 .TO>.aHS-bnt .qj {
    /*	filter: invert(49%) sepia(25%) saturate(1446%) hue-rotate(184deg) brightness(102%) contrast(79%);*/
}
/* selected */
 .TO.nZ {
    background-color: rgb(91, 134, 228, .2) !important;
}
.TO .bsU {
    color: rgb(97, 97, 97);
    font-weight: normal;
}
.TO.nZ>.aHS-bnt a {
    color: rgb(91, 134, 228) !important;
}
/* starred */
 .TO>.aHS-bnw .qj {
    filter: invert(89%) sepia(6%) saturate(2885%) hue-rotate(343deg) brightness(92%) contrast(107%);
}
/* snoozed */
 .TO>.aHS-bu1 .qj {
    filter: invert(60%) sepia(93%) saturate(1240%) hue-rotate(331deg) brightness(94%) contrast(90%);
}
/* important */
 .TO>.aHS-bns .qj {
    filter: invert(89%) sepia(6%) saturate(2885%) hue-rotate(343deg) brightness(92%) contrast(107%);
}
/* chats */
 .TO>.aHS-aHP .qj {
    filter: invert(51%) sepia(22%) saturate(1029%) hue-rotate(85deg) brightness(97%) contrast(81%);
}
/* sent */
 .TO>.aHS-bnu .qj {
    filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* drafts  */
 .TO>.aHS-bnq .qj {
    filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* all mail  */
 .TO>.aHS-aHO .qj {
    /* no color shift needed since this uses inbox's icon, which is already grey */
    /*filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);*/
}
/* trash */
 .TO>.aHS-bnx .qj {
    filter: invert(32%) sepia(0%) saturate(6247%) hue-rotate(38deg) brightness(126%) contrast(81%);
}
/* categories */
 .aHS-bnr .qr {
    /*	border-color: rgb(255, 255, 255);*/
    border-color: rgb(97, 97, 97);
}
.aHS-bnr .qj::before {
    /*	filter: drop-shadow(1px 1px 4px #999)*/
}
.aHS-bnr .qj::after {
    /*	filter: drop-shadow(3px 0 2px #bbb);*/
}
/* social */
 .TO.aS6 .qj {
    filter: invert(41%) sepia(88%) saturate(591%) hue-rotate(321deg) brightness(82%) contrast(94%);
}
/* updates */
 .TO.aS4 .qj {
    filter: invert(52%) sepia(18%) saturate(1139%) hue-rotate(217deg) brightness(96%) contrast(88%);
}
/* forums */
 .TO.aS3 .qj {
    filter: invert(33%) sepia(76%) saturate(664%) hue-rotate(194deg) brightness(86%) contrast(90%);
}
/* promotions */
 .TO.aS5 .qj {
    filter: invert(60%) sepia(89%) saturate(278%) hue-rotate(144deg) brightness(94%) contrast(84%);
}
/* spam */
 .TO>.aHS-bnv .qj {
    filter: invert(40%) sepia(41%) saturate(842%) hue-rotate(318deg) brightness(98%) contrast(109%);
}
/*** ICONS ***/

/* snoozed */
 .TO>.aHS-bu1 .qj {
    /*background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_upcoming_clr_24dp_r5.png) !important;*/
}
/* drafts */
 .TO>.aHS-bnq .qj {
    background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_draft_blk_24dp_r2.png) !important;
}
/* all mail */
 .TO>.aHS-aHO .qj {
    background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_custom-cluster_24px_g60_r3.png) !important;
}
/** for retina displays **/
 @media(min-resolution: 144dpi), (-webkit-min-device-pixel-ratio: 1.5) {
    /* snoozed */
    .TO>.aHS-bu1 .qj {
        /*background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_upcoming_clr_24dp_r5.png) !important;*/
    }
    /* drafts */
    .TO>.aHS-bnq .qj {
        background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_draft_blk_24dp_r2.png) !important;
    }
    /* all mail */
    .TO>.aHS-aHO .qj {
        background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_custom-cluster_24px_g60_r3.png) !important;
    }
}
/*** main section ***/

/** list area */
 .Tm.aeJ {
    overflow: auto;
    /*	padding-right: 4px;*/
}
/** list item **/
 .zA {
    background: white;
    /*	box-shadow: 0 -1px 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .2);*/
    /*	margin-left: 4px;*/
}
.zA span {
    font-size: 9pt;
}
/* hover */
 .zA.aqw {
    background-color: rgba(0, 123, 255, .1) !important;
    /*	box-shadow: 0 0 1px #aaa, 0 -1px 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .2);*/
    box-shadow: inset 0 0 0 1px rgba(0, 123, 255, .5);
    z-index: 1;
}
/* read */
 .zA.yO {
}
/* unread */
 .zA.zE {
}
/* thread count */
 span.bx0 {
    font-size: 6pt;
    color: rgb(255, 0, 255);
    -webkit-font-smoothing: antialiased !important;
    margin-left: 7px !important;
}
/* bottom info */
 div.l2.pfiaof {
    display: none;
}
/*** right side menu ***/
 .brC-aT5-aOt-Jw {
    background: transparent !important;
}
/*** hangouts window ***/
 .aJn {
    z-index: 1000;
}
