.is-article-page .teaser--position-1:after
 { background-color: var(--footer-devider-1a) !important; }
.is-article-page .teaser--position-1:before
{ background-color: var(--footer-devider-1b) !important; }
.is-article-page .teaser--position-3:after
 { background-color: var(--footer-devider-3a) !important; }

/*meer nieuws & achtergrond*/
/*line*/
.is-article-page .teaser-block
 { border-bottom-color: var(--footer-devider) !important; }
/*wolk cloud*/
.is-article-page a.read-more__link.fjs-read-more-link
 { background-color: var(--background-grey); border: solid 1px #000; }
.is-article-page a.read-more__link.fjs-read-more-link:hover
 { background-color: var(--background-light-grey); border: solid 1px #000; }

/********************************/
/*  Eind nieuws & achtergrond   */
/********************************/

/* disclaimer */
.is-article-page hr.app-disclaimer__line
 { background-color: var(--footer-devider); }
.is-article-page img.app-disclaimer__image
 { background-color: var(--background-light-grey); border: solid 1px #000; box-shadow: 0 0 1px 1px #000 inset; }

/*copyright*/
.is-article-page p.app-disclaimer__text
 { color: var(--color); }

/*nieuwsbrief*/
.is-article-page article#pexi-tm-22226__NEWSLETTER_BOTTOM
 { background-color: var(--background-darker-grey); border-top: solid 1px #000; }
.is-article-page article#pexi-tm-22226__NEWSLETTER_BOTTOM p
 { color: var(--color); }
.is-article-page article#pexi-tm-22226__NEWSLETTER_BOTTOM a.pexi-tm-22226__tm-call2action 
 { color: var(--color); }
/*close*/
.is-article-page article#pexi-tm-22226__NEWSLETTER_BOTTOM button.pexi-tm-22226__tm-close.pexi-tm-22226__fjs-tm-close svg.pexi-tm-22226__tm-close__icon path
 { color: #aaa !important; }



